--afb700ukr.Cafb700ukr[fnAfb700QDeposit] Query01 BEGIN DECLARE @COMP_CODE NVARCHAR(08) -- (필수) 법인코드 , @PAY_DIVI NVARCHAR(50) -- (필수) 결제방법
-- [ 변수 값 할당 ] --------------------------------------------------------------------------------------------------" SET @COMP_CODE = #{S_COMP_CODE} SET @PAY_DIVI = #{PAY_DIVI}
-- [ 데이터 조회 ] ---------------------------------------------------------------------------------------------------" SELECT ISNULL(B.BANK_CODE, '') AS IN_BANK_CODE , ISNULL(C.CUSTOM_NAME, '') AS IN_BANK_NAME , ISNULL(uniLITE.fnCipherDecrypt(B.BANK_ACCOUNT, ''), '') AS IN_BANKBOOK_NUM , ISNULL(B.SAVE_NAME, '') AS IN_BANKBOOK_NAME FROM BSA100T A WITH (NOLOCK) LEFT JOIN AFS100T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.SAVE_CODE = ISNULL(A.REF_CODE3, '') LEFT JOIN BCM100T C WITH (NOLOCK) ON C.COMP_CODE = B.COMP_CODE AND C.CUSTOM_CODE = B.BANK_CODE AND C.CUSTOM_TYPE = N'4' WHERE A.COMP_CODE = @COMP_CODE AND A.MAIN_CODE = N'A172' AND A.SUB_CODE = @PAY_DIVI END

--afb700ukr.Cafb700ukr[fnAfb700Qpop3] QUERY01 BEGIN DECLARE @COMP_CODE NVARCHAR(08) , @PAY_DRAFT_NO NVARCHAR(20)
SET @COMP_CODE = #{S_COMP_CODE} SET @PAY_DRAFT_NO = #{PAY_DRAFT_NO}
SELECT * FROM AFB711T WITH (NOLOCK) WHERE COMP_CODE = @COMP_CODE AND PAY_DRAFT_NO = @PAY_DRAFT_NO END

--afb700ukr.Cafb700ukr[fnAfb700Qpop3] QUERY03 BEGIN DECLARE @COMP_CODE NVARCHAR(08) , @PAY_DRAFT_NO NVARCHAR(20) , @PAY_YYMM NVARCHAR(06) , @SUPP_TYPE NVARCHAR(02) , @COST_POOL NVARCHAR(10) , @PERSON_GROUP NVARCHAR(10) , @AFFIL_CODE NVARCHAR(10) , @FR_DEPT_CODE NVARCHAR(08) , @FR_DEPT_NAME NVARCHAR(30) , @TO_DEPT_CODE NVARCHAR(08) , @TO_DEPT_NAME NVARCHAR(30)
-- 변수 값 할당 ------------------------------------------------------------------------------------------ SET @COMP_CODE = #{S_COMP_CODE} SET @PAY_DRAFT_NO = #{PAY_DRAFT_NO} SET @PAY_YYMM = #{PAY_YYMM} SET @SUPP_TYPE = #{SUPP_TYPE} SET @COST_POOL = #{COST_POOL} SET @PERSON_GROUP = #{PERSON_GROUP} SET @AFFIL_CODE = #{AFFIL_CODE} SET @FR_DEPT_CODE = #{FR_DEPT_CODE} SET @FR_DEPT_NAME = #{FR_DEPT_NAME} SET @TO_DEPT_CODE = #{TO_DEPT_CODE} SET @TO_DEPT_NAME = #{TO_DEPT_NAME}
-- 데이터 조회 ------------------------------------------------------------------------------------------- SELECT A.COMP_CODE , A.PAY_DRAFT_NO , A.PAY_YYYYMM , A.SUPP_TYPE , ISNULL(A.COST_POOL , '') AS COST_POOL , ISNULL(A.PERSON_GROUP, '') AS PERSON_GROUP , ISNULL(A.AFFIL_CODE , '') AS AFFIL_CODE , ISNULL(A.FR_DEPT_CODE, '') AS FR_DEPT_CODE , ISNULL(A.FR_DEPT_NAME, '') AS FR_DEPT_NAME , ISNULL(A.TO_DEPT_CODE, '') AS TO_DEPT_CODE , ISNULL(A.TO_DEPT_NAME, '') AS TO_DEPT_NAME , ISNULL(B.DED_AMT_TOT, 0) AS DED_AMT_TOT , #{sExistYN} AS EXISTS_YN FROM AFB711T A WITH (NOLOCK) LEFT JOIN (SELECT COMP_CODE , PAY_DRAFT_NO , SUM(ISNULL(DED_AMOUNT_I, 0)) AS DED_AMT_TOT FROM AFB711T WITH (NOLOCK) WHERE COMP_CODE = @COMP_CODE AND PAY_DRAFT_NO = @PAY_DRAFT_NO GROUP BY COMP_CODE, PAY_DRAFT_NO ) B ON B.COMP_CODE = A.COMP_CODE AND B.PAY_DRAFT_NO = A.PAY_DRAFT_NO WHERE A.COMP_CODE = @COMP_CODE AND A.PAY_DRAFT_NO = @PAY_DRAFT_NO END

--afb700ukr.Cafb700ukr[fnAfb700Qpop3] QUERY03 BEGIN DECLARE @COMP_CODE NVARCHAR(08) , @PAY_DRAFT_NO NVARCHAR(20) , @PAY_YYMM NVARCHAR(06) , @SUPP_TYPE NVARCHAR(02) , @COST_POOL NVARCHAR(10) , @PERSON_GROUP NVARCHAR(10) , @AFFIL_CODE NVARCHAR(10) , @FR_DEPT_CODE NVARCHAR(08) , @FR_DEPT_NAME NVARCHAR(30) , @TO_DEPT_CODE NVARCHAR(08) , @TO_DEPT_NAME NVARCHAR(30)
-- [ 변수 값 할당 ] ------------------------------------------------------------------------------------------ SET @COMP_CODE = #{S_COMP_CODE} SET @PAY_DRAFT_NO = #{PAY_DRAFT_NO} SET @PAY_YYMM = #{PAY_YYMM} SET @SUPP_TYPE = #{SUPP_TYPE} SET @COST_POOL = #{COST_POOL} SET @PERSON_GROUP = #{PERSON_GROUP} SET @AFFIL_CODE = #{AFFIL_CODE} SET @FR_DEPT_CODE = #{FR_DEPT_CODE} SET @FR_DEPT_NAME = #{FR_DEPT_NAME} SET @TO_DEPT_CODE = #{TO_DEPT_CODE} SET @TO_DEPT_NAME = #{TO_DEPT_NAME}
-- [ 데이터 조회 ] ------------------------------------------------------------------------------------------- SELECT ROW_NUMBER() OVER(PARTITION BY B.COMP_CODE ORDER BY B.REF_CODE2) AS SEQ , A.COMP_CODE , A.PAY_DRAFT_NO , A.DED_CODE , B.CODE_NAME AS DED_NAME , A.DED_AMOUNT_I , A.DED_AMOUNT_I AS DED_AMOUNT_I_DUMMY , A.PAY_YYYYMM , A.SUPP_TYPE , A.COST_POOL , A.PERSON_GROUP , A.AFFIL_CODE , A.FR_DEPT_CODE , A.FR_DEPT_NAME , A.TO_DEPT_CODE , A.TO_DEPT_NAME , A.INSERT_DB_USER , A.INSERT_DB_TIME , A.UPDATE_DB_USER , A.UPDATE_DB_TIME , '' AS UPDATE_MODE FROM AFB711T A WITH (NOLOCK) LEFT JOIN BSA100T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.MAIN_CODE = 'H034' -- 공제코드 AND B.SUB_CODE = A.DED_CODE WHERE A.COMP_CODE = @COMP_CODE AND A.PAY_DRAFT_NO = @PAY_DRAFT_NO ORDER BY B.REF_CODE2 END

