/*afb600ukr.Cafb600UKR[fnafb600q] Query02*/ DECLARE @COMP_CODE NVARCHAR(08) , @MAIN_CODE NVARCHAR(10) , @REF_CODE1 NVARCHAR(01) , @SUB_CODE NVARCHAR(01)
SET @COMP_CODE = #{S_COMP_CODE} SET @MAIN_CODE = N'A151'
/* 자동채번유무*/ SELECT TOP 1 @REF_CODE1 = ISNULL(REF_CODE1, 'N') FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @COMP_CODE AND MAIN_CODE = @MAIN_CODE AND ISNULL(REF_CODE1, 'N') = 'Y'
SET @REF_CODE1 = ISNULL(@REF_CODE1, 'N')
/* 회계기준사용(1:K-GAAP, 2:K-IFRS)*/ SELECT TOP 1 @SUB_CODE = ISNULL(SUB_CODE, '1') FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @COMP_CODE AND MAIN_CODE = @MAIN_CODE AND ISNULL(REF_CODE2, '') = 'Y'
SET @SUB_CODE = ISNULL(@SUB_CODE, '1')
SELECT @REF_CODE1 AS REF_CODE1 , @SUB_CODE AS SUB_CODE
