mapper.select:
/* bcm104ukrv.Cbcm104ukrv[fnBcm104QStd] Query02*/
BEGIN
DECLARE @REF_ITEM NVARCHAR(1)
SELECT @REF_ITEM = ISNULL(REF_ITEM, '1') FROM BSA300T WITH (NOLOCK) WHERE USER_ID = #{S_USER_ID}
DECLARE @COMP_CODE NVARCHAR(08) ,@CUSTOM_CODE NVARCHAR(20) ,@UNPAY_AMT NUMERIC(30,6) ,@STOCK_I NUMERIC(30,6)
SET @COMP_CODE = #{S_COMP_CODE} SET @CUSTOM_CODE = #{CUSTOM_CODE}
SELECT A.CUSTOM_CODE , A.CUSTOM_TYPE , A.CUSTOM_NAME , A.CUSTOM_NAME1 , A.CUSTOM_NAME2 , A.CUSTOM_FULL_NAME , A.NATION_CODE , (SUBSTRING(A.COMPANY_NUM,1,3) + '-' + SUBSTRING(A.COMPANY_NUM,4,2) + '-' + SUBSTRING(A.COMPANY_NUM,6,5)) AS COMPANY_NUM , unilite.fnCipherDecrypt(A.TOP_NUM, 'A') AS TOP_NUM , A.TOP_NAME , A.BUSINESS_TYPE , A.USE_YN , A.COMP_TYPE , A.COMP_CLASS , A.AGENT_TYPE , A.AGENT_TYPE2 , A.AGENT_TYPE3
, A.AREA_TYPE , (SUBSTRING(A.ZIP_CODE, 1, 3) + '-' + SUBSTRING(A.ZIP_CODE, 4, 3)) AS ZIP_CODE , A.ADDR1 , A.ADDR2 , A.TELEPHON , A.FAX_NUM , A.HTTP_ADDR , A.MAIL_ID , A.WON_CALC_BAS , uniLITE.fnGetUserDate(A.COMP_CODE, START_DATE) AS START_DATE , uniLITE.fnGetUserDate(A.COMP_CODE,STOP_DATE) AS STOP_DATE , A.TO_ADDRESS , A.TAX_CALC_TYPE , A.RECEIPT_DAY , A.MONEY_UNIT , A.TAX_TYPE , A.BILL_TYPE , A.SET_METH , A.VAT_RATE , A.TRANS_CLOSE_DAY , A.COLLECT_DAY , A.CREDIT_YN , A.TOT_CREDIT_AMT , A.CREDIT_AMT , uniLITE.fnGetUserDate(A.COMP_CODE, CREDIT_YMD) AS CREDIT_YMD , ISNULL(COLLECT_CARE, '2') AS COLLECT_CARE , A.SAP_CODE , A.BUSI_PRSN , A.TAX_CALC_ORDER , A.REMARK , A.MANAGE_CUSTOM , (CASE ISNULL(A.MANAGE_CUSTOM, '') WHEN '' THEN '' ELSE (SELECT CUSTOM_NAME FROM BCM100T WITH (NOLOCK) WHERE A.COMP_CODE = COMP_CODE AND A.MANAGE_CUSTOM = CUSTOM_CODE) END) AS MCUSTOM_NAME , A.COLLECTOR_CP , (CASE ISNULL(A.COLLECTOR_CP, '') WHEN '' THEN '' ELSE (SELECT CUSTOM_NAME FROM BCM100T WITH (NOLOCK) WHERE A.COMP_CODE = COMP_CODE AND A.COLLECTOR_CP = CUSTOM_CODE) END) AS COLLECTOR_CP_NAME , A.BANK_CODE , (CASE ISNULL(A.BANK_CODE, '') WHEN '' THEN '' ELSE (SELECT CUSTOM_NAME FROM BCM100T WITH (NOLOCK) WHERE A.COMP_CODE = COMP_CODE AND A.BANK_CODE = CUSTOM_CODE AND CUSTOM_TYPE = '4') END) AS BANK_NAME , unilite.fnCipherDecrypt(A.BANKBOOK_NUM, 'B') AS BANKBOOK_NUM , A.BANKBOOK_NAME , '' AS CUST_CHK , '' AS SSN_CHK , A.UPDATE_DB_USER , A.PURCHASE_BANK , (CASE ISNULL(A.PURCHASE_BANK, '') WHEN '' THEN '' ELSE (SELECT CUSTOM_NAME FROM BCM100T WITH (NOLOCK) WHERE A.COMP_CODE = COMP_CODE AND A.PURCHASE_BANK = CUSTOM_CODE AND CUSTOM_TYPE = '4') END) AS PURBANKNAME , A.BILL_PRSN , A.HAND_PHON , A.BILL_MAIL_ID , A.CUSTOM_SALE_PRSN , A.CUSTOM_SALE_HAND_PHON , A.CUSTOM_SALE_MAIL_ID , A.BILL_PRSN2 , A.HAND_PHON2 , A.BILL_MAIL_ID2 , A.BILL_MEM_TYPE , A.ADDR_TYPE , A.COMP_CODE , A.CHANNEL , A.BILL_CUSTOM , (CASE ISNULL(A.BILL_CUSTOM, '') WHEN '' THEN '' ELSE (SELECT CUSTOM_NAME FROM BCM100T WITH (NOLOCK) WHERE A.COMP_CODE = COMP_CODE AND A.BILL_CUSTOM = CUSTOM_CODE) END) AS BILL_CUSTOM_NAME , ISNULL(A.CREDIT_OVER_YN, 'N') AS CREDIT_OVER_YN , A.DEPT_CODE , A.CUST_LEVEL1 , A.SERVANT_COMPANY_NUM , A.RETURN_CODE , A.UPDATE_DB_TIME , A.CREDIT_OVER_YN , A.DVRY_PRSN , A.DVRY_PHON , A.DVRY_LT , A.PUBLICATION_FLD1 , A.PUBLICATION_FLD2 , A.PUBLICATION_FLD3 , A.CARD_NO1 , A.CARD_NO2 , A.CARD_NO3 , A.CARD_NO4 , A.WH_CODE , B.TREE_NAME AS DEPT_NAME , C.BALANCE_DATE , C.BALANCE_PRSN FROM BCM100T A WITH(NOLOCK) LEFT JOIN ( SELECT COMP_CODE ,CUSTOM_CODE ,MAX(BALANCE_DATE) AS BALANCE_DATE ,MAX(BALANCE_PRSN) AS BALANCE_PRSN FROM MAP040T WITH (NOLOCK) WHERE COMP_CODE = #{S_COMP_CODE} GROUP BY COMP_CODE, CUSTOM_CODE ) C ON C.COMP_CODE = A.COMP_CODE AND C.CUSTOM_CODE = A.CUSTOM_CODE
LEFT JOIN BSA210T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DEPT_CODE = B.TREE_CODE WHERE A.CUSTOM_CODE IS NOT NULL
AND A.CUSTOM_LEVEL1 = #{CUSTOM_LEVEL1}
AND A.CUSTOM_LEVEL2 = = #{CUSTOM_LEVEL2}
AND A.CUSTOM_LEVEL3 = = #{CUSTOM_LEVEL3}
AND A.CUSTOM_CODE LIKE #{CUSTOM_CODE} + '%'
AND ( (@REF_ITEM = '0' AND A.CUSTOM_NAME LIKE '%'+ #{CUSTOM_NAME} +'%') OR (@REF_ITEM = '1' AND A.CUSTOM_NAME1 LIKE '%'+ #{CUSTOM_NAME} +'%') OR (@REF_ITEM = '2' AND A.CUSTOM_NAME2 LIKE '%'+ #{CUSTOM_NAME} +'%' ) )
AND A.CUSTOM_TYPE = #{CUSTOM_TYPE}
AND A.AGENT_TYPE = #{AGENT_TYPE}
AND A.AREA_TYPE = #{AREA_TYPE}
AND A.BUSI_PRSN = #{BUSI_PRSN}
AND A.USE_YN = #{USE_YN}
AND A.BUSINESS_TYPE = #{BUSINESS_TYPE}
AND A.TOP_NAME LIKE #{TOP_NAME} +'%'
AND A.COMPANY_NUM LIKE #{COMPANY_NUM} + '%'
AND A.COMP_CODE = #{S_COMP_CODE}
AND A.COMPANY_NUM LIKE '%' + #{COMPANY_NUM} + '%'
ORDER BY A.CUSTOM_CODE
END

SELECT A.COMP_CODE, A.ITEM_CODE, A.DIV_CODE, --D.ITEM_NAME, F.ITEM_NAME, F.PUBLISHER_CODE, F.PUBLISHER, F.AUTHOR1, F.SALE_BASIS_P, MIN(ISNULL(A.ITEM_P,0)) AS PURCHASE_P, MIN(ISNULL(A.PURCHASE_RATE,0)) AS PURCHASE_RATE, CASE WHEN F.STOCK_CARE_YN = 'Y' THEN SUM(B.STOCK_Q) ELSE 0 END AS STOCK_Q, MIN(ISNULL(A.ITEM_P,0)) * (CASE WHEN F.STOCK_CARE_YN = 'Y' THEN SUM(B.STOCK_Q) ELSE 0 END) AS STOCK_I, D.ITEM_ACCOUNT, D.SUPPLY_TYPE, F.STOCK_CARE_YN FROM BPR400T A WITH (NOLOCK) LEFT JOIN BCM100T C WITH(NOLOCK) ON A.COMP_CODE = C.COMP_CODE AND A.CUSTOM_CODE = C.CUSTOM_CODE INNER JOIN BPR200T D WITH(NOLOCK) ON A.COMP_CODE = D.COMP_CODE AND A.DIV_CODE = D.DIV_CODE AND A.ITEM_CODE = D.ITEM_CODE INNER JOIN BPR100T F WITH(NOLOCK) ON F.COMP_CODE = A.COMP_CODE AND F.ITEM_CODE = A.ITEM_CODE AND F.USE_YN = 'Y' LEFT JOIN (SELECT A.COMP_CODE, A.DIV_CODE, A.PURCHASE_CUSTOM_CODE, A.ITEM_CODE, SUM(ISNULL(A.STOCK_Q, 0)) AS STOCK_Q FROM (SELECT A.COMP_CODE, A.DIV_CODE, A.WH_CODE, A.ITEM_CODE, B.PURCHASE_CUSTOM_CODE, B.PURCHASE_P AS ITEM_P, B.PURCHASE_RATE, A.STOCK_Q AS STOCK_Q FROM BIV150T A WITH(NOLOCK) LEFT JOIN BIV350T B WITH(NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.LOT_NO = B.LOT_NO AND A.WH_CODE = B.WH_CODE AND A.ITEM_CODE = B.ITEM_CODE WHERE A.COMP_CODE = #{S_COMP_CODE}
AND A.DIV_CODE = #{DIV_CODE}
UNION ALL SELECT A.COMP_CODE, A.DIV_CODE, A.WH_CODE, A.ITEM_CODE, A.PURCHASE_CUSTOM_CODE, A.PURCHASE_P AS ITEM_P, A.PURCHASE_RATE, A.STOCK_Q AS STOCK_Q FROM UNILITE.VGetDailyStock A WITH(NOLOCK) WHERE A.COMP_CODE = #{S_COMP_CODE}
AND A.DIV_CODE = #{DIV_CODE}
) A GROUP BY A.COMP_CODE, A.DIV_CODE, A.PURCHASE_CUSTOM_CODE, A.ITEM_CODE ) B ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.CUSTOM_CODE = B.PURCHASE_CUSTOM_CODE AND A.ITEM_CODE = B.ITEM_CODE WHERE A.COMP_CODE = #{S_COMP_CODE}
AND A.DIV_CODE = #{DIV_CODE}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND A.APLY_START_DATE = (SELECT TOP 1 MAX(X.APLY_START_DATE) AS APLY_START_DATE FROM BPR400T X WITH(NOLOCK) WHERE X.COMP_CODE = A.COMP_CODE AND X.TYPE = A.TYPE AND X.DIV_CODE = A.DIV_CODE AND X.ITEM_CODE = A.ITEM_CODE AND X.CUSTOM_CODE = A.CUSTOM_CODE AND X.MONEY_UNIT = A.MONEY_UNIT AND X.ORDER_UNIT = A.ORDER_UNIT AND X.APLY_START_DATE <= CONVERT(NVARCHAR(08), GETDATE(), 112) )
GROUP BY A.COMP_CODE, A.ITEM_CODE, A.DIV_CODE, F.ITEM_NAME ,A.CUSTOM_CODE, C.CUSTOM_NAME, F.SALE_BASIS_P ,F.PUBLISHER_CODE, F.PUBLISHER, F.AUTHOR1, F.STOCK_CARE_YN, D.ITEM_ACCOUNT, D.SUPPLY_TYPE
--HAVING SUM(A.STOCK_Q) != 0
ORDER BY F.ITEM_NAME

		/* bcm104ukrv.Cbcm104ukrv[fnBcm104Save] Query06 */
        	SELECT COUNT(CUSTOM_CODE)   as CNT
           	FROM   BCM100T  WITH (NOLOCK)
         	WHERE  COMP_CODE = #{S_COMP_CODE}
         	  AND  CUSTOM_CODE = #{CUSTOM_CODE}

		/* bcm104ukrv.Cbcm104ukrv[fnBcm104Save] Query04 */
    	SELECT  TOP 1 A.CUSTOM_NAME
        FROM    BCM100T   A  WITH (NOLOCK)
       	WHERE   A.COMP_CODE      = #{COMP_CODE}
          AND   A.CUSTOM_CODE    = #{CUSTOM_CODE}


DECLARE @COMP_CODE NVARCHAR(10), @AGENT_TYPE NVARCHAR(10), @sAUTO_CUSTOM_CODE NVARCHAR(20)
SET @COMP_CODE = #{S_COMP_CODE}
SET @AGENT_TYPE = #{AGENT_TYPE}  
EXEC uniLITE.SP_GetCustomCodeYsu @COMP_CODE, @AGENT_TYPE, @sAUTO_CUSTOM_CODE OUTPUT
SELECT @sAUTO_CUSTOM_CODE	AS	CUSTOM_CODE

