mapper.select:
DECLARE @RefCode1 NVARCHAR(50) ,@AutoCustomCode NVARCHAR(70)
/* 거래처구분에 해당하는 공통코드(=B015)의 관련1 값을 거래처정보의 접두어로 사용한다 */ SELECT @RefCode1 = ISNULL(REF_CODE1,'') FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = #{S_COMP_CODE} AND MAIN_CODE = 'B015' AND SUB_CODE = #{CUSTOM_TYPE}
/* 거래처정보에서 접두어를 뺀 값중에서 숫자값인 것을 조회 MAX인 값에서 + 1 */ SELECT @AutoCustomCode = @RefCode1 + RIGHT('00000000' + CONVERT(NVARCHAR(16), ( ISNULL(MAX(RIGHT(CUSTOM_CODE, LEN(CUSTOM_CODE) - LEN(@RefCode1))),0) + 1) ), (CASE WHEN LEN(ISNULL(MAX(RIGHT(CUSTOM_CODE, LEN(CUSTOM_CODE) - LEN(@RefCode1))),N'0000')) >= LEN(ISNULL(MAX(RIGHT(CUSTOM_CODE, LEN(CUSTOM_CODE) - LEN(@RefCode1))),N'0000') + 1) THEN LEN(ISNULL(MAX(RIGHT(CUSTOM_CODE, LEN(CUSTOM_CODE) - LEN(@RefCode1))),N'0000')) ELSE LEN(ISNULL(MAX(RIGHT(CUSTOM_CODE, LEN(CUSTOM_CODE) - LEN(@RefCode1))),N'0000') + 1) END) ) FROM BCM100T WITH (NOLOCK) WHERE COMP_CODE = #{S_COMP_CODE} AND CUSTOM_CODE LIKE @RefCode1 + '%' AND ISNUMERIC(RIGHT(CUSTOM_CODE, LEN(CUSTOM_CODE) - LEN(@RefCode1))) = 1
INSERT INTO BCM100T ( COMP_CODE ,CUSTOM_CODE ,CUSTOM_TYPE ,CUSTOM_FULL_NAME ,CUSTOM_NAME ) VALUES (#{S_COMP_CODE} ,@AutoCustomCode ,#{CUSTOM_TYPE} ,#{CUSTOM_NAME} ,#{CUSTOM_NAME}) SELECT @AutoCustomCode AS CUSTOM_CODE
