mapper.select:
/* biv321skrvServiceImpl.getYearEvaluation */ DECLARE @COMP_CODE NVARCHAR(08) , @DIV_CODE NVARCHAR(08)
SET @COMP_CODE = 'MASTER' SET @DIV_CODE = '01'
SELECT A.DIV_CODE , A.YEAR_EVALUATION_YN , ISNULL(B.LAST_YYYYMM, CONVERT(NVARCHAR(08), GETDATE(), 112)) AS LAST_YYYYMM FROM BOR120T A WITH(NOLOCK) LEFT JOIN (SELECT COMP_CODE, DIV_CODE, MAX(LAST_YYYYMM) AS LAST_YYYYMM FROM BIV900T WITH(NOLOCK) WHERE COMP_CODE = @COMP_CODE GROUP BY COMP_CODE, DIV_CODE) B ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE WHERE A.COMP_CODE = @COMP_CODE

/* biv321skrvServiceImpl.getYearEvaluation */
SELECT DIV_CODE
     , YEAR_EVALUATION_YN
  FROM BOR120T WITH(NOLOCK)
 WHERE COMP_CODE = #{S_COMP_CODE}

SELECT CASE WHEN MAX(LAST_YYYYMM) < CONVERT(VARCHAR(6), GETDATE(),112) THEN CONVERT(VARCHAR(6), GETDATE(),112) ELSE MAX(LAST_YYYYMM) END AS LAST_YYYYMM
  FROM BIV900T WITH(NOLOCK)
 WHERE COMP_CODE = #{S_COMP_CODE}
   AND DIV_CODE  = #{DIV_CODE}
 GROUP BY COMP_CODE, DIV_CODE
