/* 입고내역조회 */ /* s_otr120ukrv_jw.Cs_otr120ukrv_jw[fnOtr120QStd] Query01 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */ SELECT Z.INOUT_NUM , '' AS INOUT_SEQ , Z.INOUT_METH , Z.INOUT_TYPE_DETAIL , Z.ITEM_CODE , Z.ITEM_NAME , Z.ITEM_ACCOUNT , Z.SPEC , Z.ORDER_UNIT , SUM(Z.ORDER_UNIT_Q) AS ORDER_UNIT_Q , Z.ITEM_STATUS , SUM(Z.ORIGINAL_Q) AS ORIGINAL_Q , Z.GOOD_STOCK_Q , Z.BAD_STOCK_Q --, Z.ORDER_Q_FOR_CAL - SUM(Z.INSTOCK_Q_FOR_CAL) AS NOINOUT_Q , Z.NOINOUT_Q , Z.PRICE_YN , Z.MONEY_UNIT , Z.INOUT_FOR_P , SUM(Z.INOUT_FOR_O) AS INOUT_FOR_O , Z.ORDER_UNIT_FOR_P , SUM(Z.ORDER_UNIT_FOR_O) AS ORDER_UNIT_FOR_O , Z.ACCOUNT_YNC , Z.EXCHG_RATE_O , Z.INOUT_P , SUM(Z.INOUT_I) AS INOUT_I , Z.ORDER_UNIT_P , SUM(Z.ORDER_UNIT_I) AS ORDER_UNIT_I , Z.STOCK_UNIT , Z.TRNS_RATE , SUM(Z.INOUT_Q) AS INOUT_Q , Z.ORDER_TYPE , Z.LC_NUM , Z.BL_NUM , Z.ORDER_NUM , Z.ORDER_SEQ , SUM(Z.ORDER_Q) AS ORDER_Q , Z.INOUT_CODE_TYPE , Z.WH_CODE , Z.WH_CELL_CODE , Z.WH_CELL_NAME , Z.INOUT_DATE , Z.INOUT_PRSN , SUM(Z.ACCOUNT_Q) AS ACCOUNT_Q , Z.CREATE_LOC , Z.SALE_C_DATE , Z.REMARK , Z.PROJECT_NO --, Z.LOT_NO , Z.INOUT_TYPE , Z.INOUT_CODE , Z.DIV_CODE , Z.CUSTOM_NAME , Z.COMPANY_NUM , SUM(Z.INSTOCK_Q) AS INSTOCK_Q , Z.SALE_DIV_CODE , Z.SALE_CUSTOM_CODE , Z.BILL_TYPE , Z.SALE_TYPE , '' AS UPDATE_DB_USER , '' AS UPDATE_DB_TIME , Z.EXCESS_RATE , Z.INSPEC_NUM , Z.INSPEC_SEQ , Z.COMP_CODE , Z.BASIS_NUM , Z.BASIS_SEQ , Z.SCM_FLAG_YN , Z.PACK_QTY FROM ( SELECT A.INOUT_NUM , A.INOUT_SEQ , A.INOUT_METH , A.INOUT_TYPE_DETAIL , A.ITEM_CODE , (CASE WHEN @RefItem = '1' THEN C1.ITEM_NAME1 WHEN @RefItem = '2' THEN C1.ITEM_NAME2 ELSE C1.ITEM_NAME END) AS ITEM_NAME , C2.ITEM_ACCOUNT , C1.SPEC , ISNULL(A.ORDER_UNIT, C1.STOCK_UNIT) AS ORDER_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_Q, 0), 'M_FSET_QS') AS ORDER_UNIT_Q , A.ITEM_STATUS , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_Q, 0), 'M_FSET_QS') AS ORIGINAL_Q , uniLITE.fnFormat(@CompCode, ISNULL(C.GOOD_STOCK_Q, 0), 'M_FSET_QS') AS GOOD_STOCK_Q , uniLITE.fnFormat(@CompCode, ISNULL(C.BAD_STOCK_Q, 0), 'M_FSET_QS') AS BAD_STOCK_Q --미입고량 계산을 위한 값 -- , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q,0), 'M_FSET_QS') AS ORDER_Q_FOR_CAL -- , uniLITE.fnFormat(@CompCode, ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS INSTOCK_Q_FOR_CAL , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q,0) - ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS NOINOUT_Q , A.PRICE_YN , A.MONEY_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_P, 0), 'M_FSET_PS') AS INOUT_FOR_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_O, 0), 'M_FSET_OS') AS INOUT_FOR_O , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_FOR_P, 0), 'M_FSET_PS') AS ORDER_UNIT_FOR_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_O, 0), 'M_FSET_OS') AS ORDER_UNIT_FOR_O , A.ACCOUNT_YNC , uniLITE.fnFormat(@CompCode, ISNULL(A.EXCHG_RATE_O, 0), 'M_FSET_RS') AS EXCHG_RATE_O , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_P, 0), 'M_FSET_PS') AS INOUT_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_I, 0), 'M_FSET_IS') AS INOUT_I , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_P, 0), 'M_FSET_PS') AS ORDER_UNIT_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_I, 0), 'M_FSET_IS') AS ORDER_UNIT_I , C1.STOCK_UNIT , A.TRNS_RATE , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_Q, 0), 'M_FSET_QS') AS INOUT_Q , A.ORDER_TYPE , A.LC_NUM , A.BL_NUM , A.ORDER_NUM , A.ORDER_SEQ , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q, 0), 'M_FSET_QS') AS ORDER_Q , A.INOUT_CODE_TYPE , A.WH_CODE , A.WH_CELL_CODE , C4.WH_CELL_NAME , (CASE WHEN ISNULL(A.INOUT_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.INOUT_DATE, 1, 4)) , 'MM' , SUBSTRING(A.INOUT_DATE, 5, 2)) , 'DD' , SUBSTRING(A.INOUT_DATE, 7, 2)) END) AS INOUT_DATE , A.INOUT_PRSN , uniLITE.fnFormat(@CompCode, ISNULL(A.ACCOUNT_Q, 0), 'M_FSET_QS') AS ACCOUNT_Q , A.CREATE_LOC , (CASE WHEN ISNULL(A.SALE_C_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.SALE_C_DATE, 1, 4)) , 'MM' , SUBSTRING(A.SALE_C_DATE, 5, 2)) , 'DD' , SUBSTRING(A.SALE_C_DATE, 7, 2)) END) AS SALE_C_DATE , A.REMARK , A.PROJECT_NO , A.LOT_NO , A.INOUT_TYPE , A.INOUT_CODE , A.DIV_CODE , (CASE WHEN @RefItem = '1' THEN C3.CUSTOM_NAME1 WHEN @RefItem = '2' THEN C3.CUSTOM_NAME2 ELSE C3.CUSTOM_NAME END) AS CUSTOM_NAME , C3.COMPANY_NUM , uniLITE.fnFormat(@CompCode, ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS INSTOCK_Q , A.SALE_DIV_CODE , A.SALE_CUSTOM_CODE , A.BILL_TYPE , A.SALE_TYPE , A.UPDATE_DB_USER , A.UPDATE_DB_TIME , uniLITE.fnFormat(@CompCode, C2.EXCESS_RATE, 'M_FSET_RS') AS EXCESS_RATE , A.INSPEC_NUM , ISNULL(A.INSPEC_SEQ,0) AS INSPEC_SEQ , A.COMP_CODE , A.BASIS_NUM , ISNULL(A.BASIS_SEQ,0) AS BASIS_SEQ , A.SCM_FLAG_YN , CASE WHEN ISNULL(C2.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(C2.PACK_QTY, 1) END AS PACK_QTY -- , ISNULL(C2.PACK_QTY, 1) AS PACK_QTY FROM BTR100T A WITH (NOLOCK) LEFT JOIN MPO200T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.ORDER_NUM = A.ORDER_NUM AND B.ORDER_SEQ = A.ORDER_SEQ LEFT JOIN BIV100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.DIV_CODE = A.DIV_CODE AND C.WH_CODE = A.WH_CODE AND C.ITEM_CODE = A.ITEM_CODE INNER JOIN BPR100T C1 WITH (NOLOCK) ON C1.COMP_CODE = A.COMP_CODE AND C1.ITEM_CODE = A.ITEM_CODE INNER JOIN BPR200T C2 WITH (NOLOCK) ON C2.COMP_CODE = A.COMP_CODE AND C2.DIV_CODE = A.DIV_CODE AND C2.ITEM_CODE = A.ITEM_CODE INNER JOIN BCM100T C3 WITH (NOLOCK) ON C3.COMP_CODE = A.COMP_CODE AND C3.CUSTOM_CODE = A.INOUT_CODE LEFT JOIN BSA225T C4 WITH (NOLOCK) ON C4.COMP_CODE = A.COMP_CODE AND C4.DIV_CODE = A.DIV_CODE AND C4.WH_CODE = A.WH_CODE AND C4.WH_CELL_CODE = A.WH_CELL_CODE WHERE A.COMP_CODE = @CompCode
AND A.DIV_CODE = #{DIV_CODE}
AND A.INOUT_NUM = #{INOUT_NUM}
AND A.INOUT_TYPE = N'1' AND A.CREATE_LOC = N'2' AND A.INOUT_CODE_TYPE = N'5' ) Z GROUP BY Z.INOUT_NUM , Z.INOUT_METH , Z.ITEM_CODE , Z.NOINOUT_Q , Z.INOUT_TYPE_DETAIL , Z.ITEM_NAME , Z.ITEM_ACCOUNT, Z.SPEC , Z.ORDER_UNIT , Z.ITEM_STATUS , Z.GOOD_STOCK_Q , Z.BAD_STOCK_Q , Z.PRICE_YN , Z.MONEY_UNIT , Z.INOUT_FOR_P , Z.ORDER_UNIT_FOR_P , Z.ACCOUNT_YNC , Z.EXCHG_RATE_O , Z.INOUT_P , Z.ORDER_UNIT_P , Z.STOCK_UNIT , Z.TRNS_RATE , Z.ORDER_TYPE , Z.LC_NUM , Z.BL_NUM , Z.ORDER_NUM , Z.ORDER_SEQ , Z.INOUT_CODE_TYPE , Z.WH_CODE , Z.WH_CELL_CODE , Z.WH_CELL_NAME , Z.INOUT_DATE , Z.INOUT_PRSN , Z.CREATE_LOC , Z.SALE_C_DATE , Z.REMARK , Z.PROJECT_NO , Z.INOUT_TYPE , Z.INOUT_CODE , Z.DIV_CODE , Z.CUSTOM_NAME , Z.COMPANY_NUM , Z.SALE_DIV_CODE , Z.SALE_CUSTOM_CODE , Z.BILL_TYPE , Z.SALE_TYPE , Z.EXCESS_RATE , Z.INSPEC_NUM , Z.INSPEC_SEQ , Z.COMP_CODE , Z.BASIS_NUM , Z.BASIS_SEQ , Z.SCM_FLAG_YN , Z.PACK_QTY --, Z.ORDER_Q_FOR_CAL, Z.LOT_NO, Z.UPDATE_DB_USER , Z.UPDATE_DB_TIME
ORDER BY Z.INOUT_DATE DESC, Z.INOUT_NUM DESC
SET NOCOUNT OFF SET ARITHABORT OFF END

/* 검색 */ /* s_otr120ukrv_jw.Cs_otr120ukrv_jw[fnOtr120QPop] Query01 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */ SELECT DISTINCT (CASE WHEN @RefItem = '1' THEN C1.CUSTOM_NAME1 WHEN @RefItem = '2' THEN C1.CUSTOM_NAME2 ELSE C1.CUSTOM_NAME END) AS INOUT_NAME , (CASE WHEN ISNULL(A.INOUT_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.INOUT_DATE, 1, 4)) , 'MM' , SUBSTRING(A.INOUT_DATE, 5, 2)) , 'DD' , SUBSTRING(A.INOUT_DATE, 7, 2)) END) AS INOUT_DATE , A.ORDER_TYPE , A.INOUT_CODE , A.WH_CODE , A.WH_CELL_CODE , C2.WH_CELL_NAME , A.DIV_CODE , A.INOUT_PRSN , A.INOUT_NUM , A.LOT_NO , A.MONEY_UNIT , A.EXCHG_RATE_O FROM BTR100T A WITH (NOLOCK) INNER JOIN BCM100T C1 WITH (NOLOCK) ON C1.COMP_CODE = A.COMP_CODE AND C1.CUSTOM_CODE = A.INOUT_CODE LEFT JOIN BSA225T C2 WITH (NOLOCK) ON C2.COMP_CODE = A.COMP_CODE AND C2.DIV_CODE = A.DIV_CODE AND C2.WH_CODE = A.WH_CODE AND C2.WH_CELL_CODE = A.WH_CELL_CODE WHERE A.COMP_CODE = @CompCode
AND A.DIV_CODE = #{DIV_CODE}
AND A.INOUT_TYPE = N'1' AND A.INOUT_CODE_TYPE = N'5'
AND A.INOUT_CODE LIKE #{INOUT_CODE}
AND C1.CUSTOM_NAME LIKE '%' + #{CUSTOM_NAME} + '%'
AND A.INOUT_DATE >= #{FR_INOUT_DATE}
AND A.INOUT_DATE <= #{TO_INOUT_DATE}
AND A.WH_CODE = #{WH_CODE}
AND A.WH_CELL_CODE = #{WH_CELL_CODE}
AND A.INOUT_PRSN = #{INOUT_PRSN}
AND A.LOT_NO LIKE #{LOT_NO}
ORDER BY INOUT_NAME, INOUT_DATE
SET NOCOUNT OFF SET ARITHABORT OFF END

/* 미입고참조 */ /*s_otr120ukrv_jw.Cs_otr120ukrv_jw[fnRtn120QRef] Query01*/ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */ , @InspecUse NVARCHAR(01) /*검사프로그램 사용여부*/ SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
SELECT TOP 1 @InspecUse = MAX(SUB_CODE) FROM BSA100T WHERE COMP_CODE = @CompCode AND MAIN_CODE = 'Q003' AND UPPER(ISNULL(LEFT(REF_CODE1,1),'')) = 'Y'
SET @InspecUse = ISNULL(@InspecUse, 'N')
/* 데이터 조회 */ SELECT CAST('' AS BIT) AS GUBUN , B.ITEM_CODE , CASE WHEN @RefItem = '0' THEN C1.ITEM_NAME WHEN @RefItem = '1' THEN C1.ITEM_NAME1 WHEN @RefItem = '2' THEN C1.ITEM_NAME2 ELSE C1.ITEM_NAME END AS ITEM_NAME , C1.SPEC , uniLITE.fnGetUserDate(B.COMP_CODE, B.DVRY_DATE) AS DVRY_DATE , A.DIV_CODE , B.ORDER_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_UNIT_Q, 0), 'M_FSET_QS') AS ORDER_UNIT_Q , uniLITE.fnFormat(@CompCode, (ISNULL(B.ORDER_UNIT_Q, 0) - (ISNULL(B.INSTOCK_Q, 0) / ISNULL(B.TRNS_RATE, 1))), 'M_FSET_QS') AS REMAIN_Q , C1.STOCK_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q, 0) - ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS NOINOUT_Q , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q, 0), 'M_FSET_QS') AS ORDER_Q , A.MONEY_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.EXCHG_RATE_O, 0), 'M_FSET_RS') AS EXCHG_RATE_O , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_P, 0), 'M_FSET_PS') AS ORDER_P , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_UNIT_P, 0), 'M_FSET_PS') AS ORDER_UNIT_P , uniLITE.fnFormat(@CompCode, (ISNULL(B.ORDER_UNIT_Q, 0) - (ISNULL(B.INSTOCK_Q, 0)/ ISNULL(B.TRNS_RATE, 1))) * ISNULL(B.ORDER_UNIT_P, 0),'M_FSET_OS') AS ORDER_O , B.ORDER_NUM , B.ORDER_SEQ , A.LC_NUM , B.WH_CODE , '' AS WH_CELL_CODE , B.ORDER_REQ_NUM , A.ORDER_TYPE , A.CUSTOM_CODE , ISNULL(B.TRNS_RATE, 1) AS TRNS_RATE , B.LOT_NO , B.REMARK , B.PROJECT_NO , CASE WHEN ISNULL(C2.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(C2.PACK_QTY, 1) END AS PACK_QTY -- , ISNULL(C2.PACK_QTY, 1) AS PACK_QTY FROM MPO100T A WITH (NOLOCK) INNER JOIN MPO200T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.ORDER_NUM = B.ORDER_NUM INNER JOIN BPR100T C1 WITH (NOLOCK) ON C1.COMP_CODE = B.COMP_CODE AND C1.ITEM_CODE = B.ITEM_CODE INNER JOIN BPR200T C2 WITH (NOLOCK) ON C2.COMP_CODE = B.COMP_CODE AND C2.DIV_CODE = B.DIV_CODE AND C2.ITEM_CODE = B.ITEM_CODE WHERE A.COMP_CODE = @CompCode
AND A.DIV_CODE = #{DIV_CODE}
AND B.CONTROL_STATUS != N'9' AND ISNULL(A.ORDER_TYPE, '') = N'4' AND B.ORDER_Q > B.INSTOCK_Q AND ISNULL(A.AGREE_STATUS, '1') > N'1' AND ( (@InspecUse = 'N') OR (@InspecUse = 'Y' AND ISNULL(B.INSPEC_FLAG, ISNULL(C2.INSPEC_YN, 'N')) = 'N') )
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND B.DVRY_DATE >= #{FR_DVRY_DATE}
AND B.DVRY_DATE <= #{TO_DVRY_DATE}
AND A.ORDER_TYPE = #{ORDER_TYPE}
AND B.WH_CODE = #{WH_CODE}
AND A.MONEY_UNIT = #{MONEY_UNIT}
ORDER BY B.ORDER_NUM DESC, B.ORDER_SEQ
SET NOCOUNT OFF SET ARITHABORT OFF END

/* 반품가능발주참조 */ /* --s_otr120ukrv_jw.Cs_otr120ukrv_jw[fnRtn120QRef] Query01 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */ SELECT CAST('' AS BIT) AS GUBUN , B.ITEM_CODE , CASE WHEN @RefItem = '0' THEN C1.ITEM_NAME WHEN @RefItem = '1' THEN C1.ITEM_NAME1 WHEN @RefItem = '2' THEN C1.ITEM_NAME2 ELSE C1.ITEM_NAME END AS ITEM_NAME , C1.SPEC , uniLITE.fnGetUserDate(B.COMP_CODE, B.DVRY_DATE) AS DVRY_DATE , A.DIV_CODE , B.ORDER_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_UNIT_Q, 0), 'M_FSET_QS') AS ORDER_UNIT_Q , uniLITE.fnFormat(@CompCode, (ISNULL(B.INSTOCK_Q, 0) / ISNULL(B.TRNS_RATE, 1)) * (-1), 'M_FSET_QS') AS REMAIN_Q , C1.STOCK_UNIT , uniLITE.fnFormat(@CompCode, ISNULL( B.INSTOCK_Q, 0) * (-1), 'M_FSET_QS') AS NOINOUT_Q , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q, 0), 'M_FSET_QS') AS ORDER_Q , A.MONEY_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.EXCHG_RATE_O, 0), 'M_FSET_RS') AS EXCHG_RATE_O , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_P, 0), 'M_FSET_PS') AS ORDER_P , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_UNIT_P, 0), 'M_FSET_PS') AS ORDER_UNIT_P , uniLITE.fnFormat(@CompCode, ((ISNULL(B.INSTOCK_Q, 0) / ISNULL(B.TRNS_RATE, 1)) * (-1)) * ISNULL(B.ORDER_UNIT_P, 0), 'M_FSET_QS') AS ORDER_O , B.ORDER_NUM , B.ORDER_SEQ , A.LC_NUM , B.WH_CODE , '' AS WH_CELL_CODE , B.ORDER_REQ_NUM , A.ORDER_TYPE , A.CUSTOM_CODE , ISNULL(B.TRNS_RATE, 1) AS TRNS_RATE , B.LOT_NO , B.REMARK , B.PROJECT_NO , C.INSPEC_NUM , C.INSPEC_SEQ , CASE WHEN ISNULL(C2.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(C2.PACK_QTY, 1) END AS PACK_QTY --, ISNULL(C2.PACK_QTY, 1) AS PACK_QTY FROM MPO100T A WITH (NOLOCK) INNER JOIN MPO200T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.ORDER_NUM = B.ORDER_NUM LEFT JOIN BTR100T C WITH (NOLOCK) ON B.COMP_CODE = C.COMP_CODE AND B.DIV_CODE = C.DIV_CODE AND B.ORDER_NUM = C.ORDER_NUM AND B.ORDER_SEQ = C.ORDER_SEQ AND C.INOUT_CODE = '1' INNER JOIN BPR100T C1 WITH (NOLOCK) ON C1.COMP_CODE = B.COMP_CODE AND C1.ITEM_CODE = B.ITEM_CODE INNER JOIN BPR200T C2 WITH (NOLOCK) ON C2.COMP_CODE = B.COMP_CODE AND C2.DIV_CODE = B.DIV_CODE AND C2.ITEM_CODE = B.ITEM_CODE WHERE A.COMP_CODE = @CompCode
AND A.DIV_CODE = #{DIV_CODE}
AND B.CONTROL_STATUS != N'9' AND ISNULL(A.ORDER_TYPE, '') = N'4' AND ISNULL(B.INSTOCK_Q, 0) > 0 AND ISNULL(A.AGREE_STATUS, '1') > N'1'
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND A.ORDER_DATE > #{FR_ORDER_DATE}
AND A.ORDER_DATE < #{TO_ORDER_DATE}
AND B.WH_CODE = #{WH_CODE}
AND A.MONEY_UNIT = #{MONEY_UNIT}
ORDER BY B.ORDER_NUM DESC, B.ORDER_SEQ
SET NOCOUNT OFF SET ARITHABORT OFF END

/* 검사결과참조 */ /* s_otr120ukrv_jw.Cs_otr120ukrv_jw[fnQms120QRef] Query01 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */ /* 외주입고시 불량반영여부 체크 */ IF EXISTS(SELECT TOP 1 REF_CODE1 FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = 'M023' AND SUB_CODE = '1' AND REF_CODE1 = 'Y') BEGIN SELECT CAST('' AS BIT) AS GUBUN , B.ITEM_CODE , (CASE WHEN @RefItem = '1' THEN C.ITEM_NAME1 WHEN @RefItem = '2' THEN C.ITEM_NAME2 ELSE C.ITEM_NAME END) AS ITEM_NAME , D.ITEM_ACCOUNT , C.SPEC , (CASE WHEN ISNULL(B.DVRY_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(B.DVRY_DATE, 1, 4)) , 'MM' , SUBSTRING(B.DVRY_DATE, 5, 2)) , 'DD' , SUBSTRING(B.DVRY_DATE, 7, 2)) END) AS DVRY_DATE , (CASE WHEN ISNULL(E.INSPEC_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(E.INSPEC_DATE, 1, 4)) , 'MM' , SUBSTRING(E.INSPEC_DATE, 5, 2)) , 'DD' , SUBSTRING(E.INSPEC_DATE, 7, 2)) END) AS INSPEC_DATE , A.DIV_CODE , B.ORDER_UNIT , B.ORDER_UNIT_Q , (ISNULL(E.GOOD_INSPEC_Q,0)) AS REMAIN_Q , C.STOCK_UNIT , (ISNULL(E.GOOD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) AS NOINOUT_Q , B.ORDER_Q , B.UNIT_PRICE_TYPE , '1' AS ITEM_STATUS , A.MONEY_UNIT , A.EXCHG_RATE_O , B.ORDER_P , B.ORDER_UNIT_P , (ISNULL(E.GOOD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) * ISNULL(B.ORDER_UNIT_P,0) AS ORDER_O , B.ORDER_NUM , B.ORDER_SEQ , A.LC_NUM , B.WH_CODE , '' AS WH_CELL_CODE , B.ORDER_REQ_NUM , A.ORDER_TYPE , A.CUSTOM_CODE ,(CASE WHEN @RefItem = '1' THEN G.CUSTOM_NAME1 WHEN @RefItem = '2' THEN G.CUSTOM_NAME2 ELSE G.CUSTOM_NAME END) AS CUSTOM_NAME , ISNULL(B.TRNS_RATE,1) AS TRNS_RATE , ISNULL(B.INSTOCK_Q,0) AS INSTOCK_Q , A.PROJECT_NO , D.EXCESS_RATE , E.INSPEC_NUM , E.INSPEC_SEQ , (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) AS PORE_Q , B.LOT_NO , E.REMARK AS INSPEC_REMARK , E.PROJECT_NO AS INSPEC_PROJECT_NO , CASE WHEN ISNULL(D.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(D.PACK_QTY, 1) END AS PACK_QTY FROM MPO100T A WITH (NOLOCK) INNER JOIN MPO200T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.ORDER_NUM = B.ORDER_NUM INNER JOIN BPR100T C WITH (NOLOCK) ON C.COMP_CODE = B.COMP_CODE AND C.ITEM_CODE = B.ITEM_CODE INNER JOIN BPR200T D WITH (NOLOCK) ON D.COMP_CODE = B.COMP_CODE AND D.DIV_CODE = B.DIV_CODE AND D.ITEM_CODE = B.ITEM_CODE INNER JOIN QMS200T E WITH (NOLOCK) ON E.COMP_CODE = B.COMP_CODE AND E.DIV_CODE = B.DIV_CODE AND E.ORDER_NUM = B.ORDER_NUM AND E.ORDER_SEQ = B.ORDER_SEQ INNER JOIN BCM100T G WITH (NOLOCK) ON G.COMP_CODE = B.COMP_CODE AND G.CUSTOM_CODE = B.CUSTOM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, ITEM_STATUS, SUM(INOUT_Q) AS INOUT_Q, INSPEC_NUM, INSPEC_SEQ FROM BTR100T WITH (NOLOCK) WHERE INSPEC_NUM IS NOT NULL AND ITEM_STATUS = N'1' AND CREATE_LOC = N'2' AND INOUT_CODE_TYPE = N'5' GROUP BY COMP_CODE, DIV_CODE, ITEM_STATUS, INSPEC_NUM, INSPEC_SEQ ) F ON E.INSPEC_NUM = F.INSPEC_NUM AND E.INSPEC_SEQ = F.INSPEC_SEQ AND E.DIV_CODE = F.DIV_CODE AND E.COMP_CODE = F.COMP_CODE WHERE A.COMP_CODE = @CompCode AND B.CONTROL_STATUS != N'9' AND ISNULL(A.ORDER_TYPE,'') = N'4' AND ISNULL(A.AGREE_STATUS,'1') > N'1' AND ISNULL(B.INSPEC_FLAG, (SELECT ISNULL(INSPEC_YN, 'N') FROM BPR200T WITH (NOLOCK) WHERE COMP_CODE = B.COMP_CODE AND DIV_CODE = B.DIV_CODE AND ITEM_CODE = B.ITEM_CODE)) = N'Y'
AND (ISNULL(E.GOOD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) > 0 AND (ISNULL(E.GOOD_INSPEC_Q,0)) > 0 AND (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) > 0
AND A.DIV_CODE = #{DIV_CODE}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND B.DVRY_DATE >= #{FR_DVRY_DATE}
AND B.DVRY_DATE <= #{TO_DVRY_DATE}
AND E.INSPEC_NUM = #{INSPEC_NUM}
AND A.ORDER_TYPE = #{ORDER_TYPE}
AND B.WH_CODE = #{WH_CODE}
AND A.MONEY_UNIT = #{MONEY_UNIT}
UNION ALL
SELECT CAST('' AS BIT) AS GUBUN , B.ITEM_CODE , CASE WHEN @RefItem = '0' THEN C.ITEM_NAME WHEN @RefItem = '1' THEN C.ITEM_NAME1 WHEN @RefItem = '2' THEN C.ITEM_NAME2 ELSE C.ITEM_NAME END AS ITEM_NAME , D.ITEM_ACCOUNT , C.SPEC , (CASE WHEN ISNULL(B.DVRY_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(B.DVRY_DATE, 1, 4)) , 'MM' , SUBSTRING(B.DVRY_DATE, 5, 2)) , 'DD' , SUBSTRING(B.DVRY_DATE, 7, 2)) END) AS DVRY_DATE , (CASE WHEN ISNULL(E.INSPEC_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(E.INSPEC_DATE, 1, 4)) , 'MM' , SUBSTRING(E.INSPEC_DATE, 5, 2)) , 'DD' , SUBSTRING(E.INSPEC_DATE, 7, 2)) END) AS INSPEC_DATE , A.DIV_CODE , B.ORDER_UNIT , B.ORDER_UNIT_Q , (ISNULL(E.BAD_INSPEC_Q,0)) AS REMAIN_Q , C.STOCK_UNIT , (ISNULL(E.BAD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) AS NOINOUT_Q , B.ORDER_Q , B.UNIT_PRICE_TYPE , '2' AS ITEM_STATUS , A.MONEY_UNIT , A.EXCHG_RATE_O , B.ORDER_P , B.ORDER_UNIT_P , (ISNULL(E.BAD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) * ISNULL(B.ORDER_UNIT_P,0) AS ORDER_O , B.ORDER_NUM , B.ORDER_SEQ , A.LC_NUM , B.WH_CODE , '' AS WH_CELL_CODE , B.ORDER_REQ_NUM , A.ORDER_TYPE , A.CUSTOM_CODE , (CASE WHEN @RefItem = '1' THEN G.CUSTOM_NAME1 WHEN @RefItem = '2' THEN G.CUSTOM_NAME2 ELSE G.CUSTOM_NAME END) AS CUSTOM_NAME , ISNULL(B.TRNS_RATE,1) AS TRNS_RATE , ISNULL(B.INSTOCK_Q,0) AS INSTOCK_Q , A.PROJECT_NO , D.EXCESS_RATE , E.INSPEC_NUM , E.INSPEC_SEQ , (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) AS PORE_Q , B.LOT_NO , E.REMARK AS INSPEC_REMARK , E.PROJECT_NO AS INSPEC_PROJECT_NO , CASE WHEN ISNULL(D.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(D.PACK_QTY, 1) END AS PACK_QTY
FROM MPO100T A WITH (NOLOCK) INNER JOIN MPO200T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.ORDER_NUM = B.ORDER_NUM INNER JOIN BPR100T C WITH (NOLOCK) ON C.COMP_CODE = B.COMP_CODE AND C.ITEM_CODE = B.ITEM_CODE INNER JOIN BPR200T D WITH (NOLOCK) ON D.COMP_CODE = B.COMP_CODE AND D.DIV_CODE = B.DIV_CODE AND D.ITEM_CODE = B.ITEM_CODE INNER JOIN QMS200T E WITH (NOLOCK) ON E.COMP_CODE = B.COMP_CODE AND E.DIV_CODE = B.DIV_CODE AND E.ORDER_NUM = B.ORDER_NUM AND E.ORDER_SEQ = B.ORDER_SEQ INNER JOIN BCM100T G WITH (NOLOCK) ON G.COMP_CODE = B.COMP_CODE AND G.CUSTOM_CODE = B.CUSTOM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, ITEM_STATUS, SUM(INOUT_Q) AS INOUT_Q, INSPEC_NUM, INSPEC_SEQ FROM BTR100T WITH (NOLOCK) WHERE INSPEC_NUM IS NOT NULL AND ITEM_STATUS = N'2' AND CREATE_LOC = N'2' AND INOUT_CODE_TYPE = N'5' GROUP BY COMP_CODE, DIV_CODE, ITEM_STATUS, INSPEC_NUM, INSPEC_SEQ ) F ON E.INSPEC_NUM = F.INSPEC_NUM AND E.INSPEC_SEQ = F.INSPEC_SEQ AND E.DIV_CODE = F.DIV_CODE AND E.COMP_CODE = F.COMP_CODE WHERE A.COMP_CODE = @CompCode AND B.CONTROL_STATUS != N'9' AND ISNULL(A.ORDER_TYPE,'') = N'4' AND ISNULL(A.AGREE_STATUS,'1') > N'1' AND ISNULL(B.INSPEC_FLAG, (SELECT ISNULL(INSPEC_YN, 'N') FROM BPR200T WITH (NOLOCK) WHERE COMP_CODE = B.COMP_CODE AND DIV_CODE = B.DIV_CODE AND ITEM_CODE = B.ITEM_CODE)) = N'Y'
AND (ISNULL(E.BAD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) > 0 AND (ISNULL(E.BAD_INSPEC_Q,0)) > 0 AND (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) > 0
AND A.DIV_CODE = #{DIV_CODE}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND B.DVRY_DATE >= #{FR_DVRY_DATE}
AND B.DVRY_DATE <= #{TO_DVRY_DATE}
AND E.INSPEC_NUM = #{INSPEC_NUM}
AND A.ORDER_TYPE = #{ORDER_TYPE}
AND B.WH_CODE = #{WH_CODE}
AND A.MONEY_UNIT = #{MONEY_UNIT}
ORDER BY B.ORDER_NUM DESC, B.ORDER_SEQ END ELSE BEGIN SELECT CAST('' AS BIT) AS GUBUN , B.ITEM_CODE , (CASE WHEN @RefItem = '1' THEN C.ITEM_NAME1 WHEN @RefItem = '2' THEN C.ITEM_NAME2 ELSE C.ITEM_NAME END) AS ITEM_NAME , D.ITEM_ACCOUNT , C.SPEC , (CASE WHEN ISNULL(B.DVRY_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(B.DVRY_DATE, 1, 4)) , 'MM' , SUBSTRING(B.DVRY_DATE, 5, 2)) , 'DD' , SUBSTRING(B.DVRY_DATE, 7, 2)) END) AS DVRY_DATE , UNILITE.fnGetUserdate(E.COMP_CODE, E.INSPEC_DATE) AS INSPEC_DATE , (CASE WHEN ISNULL(E.INSPEC_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(E.INSPEC_DATE, 1, 4)) , 'MM' , SUBSTRING(E.INSPEC_DATE, 5, 2)) , 'DD' , SUBSTRING(E.INSPEC_DATE, 7, 2)) END) AS INSPEC_DATE , A.DIV_CODE , B.ORDER_UNIT , B.ORDER_UNIT_Q , (ISNULL(E.GOOD_INSPEC_Q,0)) AS REMAIN_Q , C.STOCK_UNIT , (ISNULL(E.GOOD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) AS NOINOUT_Q , B.ORDER_Q , B.UNIT_PRICE_TYPE , '1' AS ITEM_STATUS , A.MONEY_UNIT , A.EXCHG_RATE_O , B.ORDER_P , B.ORDER_UNIT_P , (ISNULL(E.GOOD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) * ISNULL(B.ORDER_UNIT_P,0) AS ORDER_O , B.ORDER_NUM , B.ORDER_SEQ , A.LC_NUM , B.WH_CODE , '' AS WH_CELL_CODE , B.ORDER_REQ_NUM , A.ORDER_TYPE , A.CUSTOM_CODE , (CASE WHEN @RefItem = '1' THEN G.CUSTOM_NAME1 WHEN @RefItem = '2' THEN G.CUSTOM_NAME2 ELSE G.CUSTOM_NAME END) AS CUSTOM_NAME , ISNULL(B.TRNS_RATE,1) AS TRNS_RATE , ISNULL(B.INSTOCK_Q,0) AS INSTOCK_Q , A.PROJECT_NO , D.EXCESS_RATE , E.INSPEC_NUM , E.INSPEC_SEQ , (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) AS PORE_Q , B.LOT_NO , E.REMARK AS INSPEC_REMARK , E.PROJECT_NO AS INSPEC_PROJECT_NO , CASE WHEN ISNULL(D.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(D.PACK_QTY, 1) END AS PACK_QTY
FROM MPO100T A WITH (NOLOCK) INNER JOIN MPO200T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.ORDER_NUM = B.ORDER_NUM INNER JOIN BPR100T C WITH (NOLOCK) ON C.COMP_CODE = B.COMP_CODE AND C.ITEM_CODE = B.ITEM_CODE INNER JOIN BPR200T D WITH (NOLOCK) ON D.COMP_CODE = B.COMP_CODE AND D.DIV_CODE = B.DIV_CODE AND D.ITEM_CODE = B.ITEM_CODE INNER JOIN QMS200T E WITH (NOLOCK) ON E.COMP_CODE = B.COMP_CODE AND E.DIV_CODE = B.DIV_CODE AND E.ORDER_NUM = B.ORDER_NUM AND E.ORDER_SEQ = B.ORDER_SEQ INNER JOIN BCM100T G WITH (NOLOCK) ON G.COMP_CODE = B.COMP_CODE AND G.CUSTOM_CODE = B.CUSTOM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, ITEM_STATUS, SUM(INOUT_Q) AS INOUT_Q, INSPEC_NUM, INSPEC_SEQ FROM BTR100T WITH (NOLOCK) WHERE INSPEC_NUM IS NOT NULL AND ITEM_STATUS = N'1' AND CREATE_LOC = N'2' AND INOUT_CODE_TYPE = N'5' GROUP BY COMP_CODE, DIV_CODE, ITEM_STATUS, INSPEC_NUM, INSPEC_SEQ ) F ON E.INSPEC_NUM = F.INSPEC_NUM AND E.INSPEC_SEQ = F.INSPEC_SEQ AND E.DIV_CODE = F.DIV_CODE AND E.COMP_CODE = F.COMP_CODE
WHERE A.COMP_CODE = @CompCode
AND B.CONTROL_STATUS != N'9' AND ISNULL(A.ORDER_TYPE,'') = N'4' AND ISNULL(A.AGREE_STATUS,'1') > N'1' AND ISNULL(B.INSPEC_FLAG, (SELECT ISNULL(INSPEC_YN, 'N') FROM BPR200T WITH (NOLOCK) WHERE COMP_CODE = B.COMP_CODE AND DIV_CODE = B.DIV_CODE AND ITEM_CODE = B.ITEM_CODE)) = N'Y'
AND (ISNULL(E.GOOD_INSPEC_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) > 0 AND (ISNULL(E.GOOD_INSPEC_Q,0)) > 0 AND (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) > 0
AND A.DIV_CODE = #{DIV_CODE}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND B.DVRY_DATE >= #{FR_DVRY_DATE}
AND B.DVRY_DATE <= #{TO_DVRY_DATE}
AND E.INSPEC_NUM = #{INSPEC_NUM}
AND A.ORDER_TYPE = #{ORDER_TYPE}
AND B.WH_CODE = #{WH_CODE}
AND A.MONEY_UNIT = #{MONEY_UNIT}
ORDER BY B.ORDER_NUM DESC, B.ORDER_SEQ END
SET NOCOUNT OFF SET ARITHABORT OFF END

/* 무검사결과참조 */ /* s_otr120ukrv_jw.Cs_otr120ukrv_jw[fnQms120QRef] Query02 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */
BEGIN SELECT CAST('' AS BIT) AS GUBUN , B.ITEM_CODE , (CASE WHEN @RefItem = '1' THEN C.ITEM_NAME1 WHEN @RefItem = '2' THEN C.ITEM_NAME2 ELSE C.ITEM_NAME END) AS ITEM_NAME , D.ITEM_ACCOUNT , C.SPEC , (CASE WHEN ISNULL(B.DVRY_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(B.DVRY_DATE, 1, 4)) , 'MM' , SUBSTRING(B.DVRY_DATE, 5, 2)) , 'DD' , SUBSTRING(B.DVRY_DATE, 7, 2)) END) AS DVRY_DATE , UNILITE.fnGetUserdate(E.COMP_CODE, E.RECEIPT_DATE) AS INSPEC_DATE , (CASE WHEN ISNULL(E.RECEIPT_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(E.RECEIPT_DATE, 1, 4)) , 'MM' , SUBSTRING(E.RECEIPT_DATE, 5, 2)) , 'DD' , SUBSTRING(E.RECEIPT_DATE, 7, 2)) END) AS INSPEC_DATE , A.DIV_CODE , B.ORDER_UNIT , B.ORDER_UNIT_Q , (ISNULL(E.RECEIPT_Q,0)) AS REMAIN_Q , C.STOCK_UNIT , (ISNULL(E.RECEIPT_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) AS NOINOUT_Q , B.ORDER_Q , B.UNIT_PRICE_TYPE , '1' AS ITEM_STATUS , A.MONEY_UNIT , A.EXCHG_RATE_O , B.ORDER_P , B.ORDER_UNIT_P , (ISNULL(E.RECEIPT_Q,0) - (ISNULL(F.INOUT_Q,0) / ISNULL(B.TRNS_RATE,1))) * ISNULL(B.ORDER_UNIT_P,0) AS ORDER_O , B.ORDER_NUM , B.ORDER_SEQ , A.LC_NUM , B.WH_CODE , '' AS WH_CELL_CODE , B.ORDER_REQ_NUM , A.ORDER_TYPE , A.CUSTOM_CODE , (CASE WHEN @RefItem = '1' THEN G.CUSTOM_NAME1 WHEN @RefItem = '2' THEN G.CUSTOM_NAME2 ELSE G.CUSTOM_NAME END) AS CUSTOM_NAME , ISNULL(B.TRNS_RATE,1) AS TRNS_RATE , ISNULL(B.INSTOCK_Q,0) AS INSTOCK_Q , A.PROJECT_NO , D.EXCESS_RATE , E.RECEIPT_NUM AS INSPEC_NUM , E.RECEIPT_SEQ AS INSPEC_SEQ , (ISNULL(B.ORDER_UNIT_Q,0) - (ISNULL(B.INSTOCK_Q,0) / ISNULL(B.TRNS_RATE,1))) AS PORE_Q , B.LOT_NO , E.REMARK AS INSPEC_REMARK , E.PROJECT_NO AS INSPEC_PROJECT_NO , CASE WHEN ISNULL(D.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(D.PACK_QTY, 1) END AS PACK_QTY -- , ISNULL(D.PACK_QTY, 1) AS PACK_QTY
FROM MPO100T A WITH (NOLOCK) INNER JOIN MPO200T B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.ORDER_NUM = B.ORDER_NUM INNER JOIN BPR100T C WITH (NOLOCK) ON C.COMP_CODE = B.COMP_CODE AND C.ITEM_CODE = B.ITEM_CODE INNER JOIN BPR200T D WITH (NOLOCK) ON D.COMP_CODE = B.COMP_CODE AND D.DIV_CODE = B.DIV_CODE AND D.ITEM_CODE = B.ITEM_CODE INNER JOIN QMS100T E WITH (NOLOCK) ON E.COMP_CODE = B.COMP_CODE AND E.DIV_CODE = B.DIV_CODE AND E.ORDER_NUM = B.ORDER_NUM AND E.ORDER_SEQ = B.ORDER_SEQ INNER JOIN BCM100T G WITH (NOLOCK) ON G.COMP_CODE = B.COMP_CODE AND G.CUSTOM_CODE = B.CUSTOM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, ITEM_STATUS, SUM(INOUT_Q) AS INOUT_Q, INSPEC_NUM, INSPEC_SEQ FROM BTR100T WITH (NOLOCK) WHERE INSPEC_NUM IS NOT NULL AND ITEM_STATUS = N'1' AND CREATE_LOC = N'2' AND INOUT_CODE_TYPE = N'5' GROUP BY COMP_CODE, DIV_CODE, ITEM_STATUS, INSPEC_NUM, INSPEC_SEQ ) F ON E.RECEIPT_NUM = F.INSPEC_NUM AND E.RECEIPT_SEQ = F.INSPEC_SEQ AND E.DIV_CODE = F.DIV_CODE AND E.COMP_CODE = F.COMP_CODE LEFT JOIN BSA100T S WITH(NOLOCK) ON S.COMP_CODE = A.COMP_CODE AND S.MAIN_CODE = 'M102' AND S.SUB_CODE = '1'
WHERE A.COMP_CODE = @CompCode
AND B.CONTROL_STATUS != N'9' AND ISNULL(A.ORDER_TYPE,'') = N'4' AND ISNULL(A.AGREE_STATUS,'1') > N'1' AND ISNULL(E.INSPEC_FLAG, 'Y') = N'N' AND ISNULL(E.RECEIPT_Q, 0) > 0 AND ISNULL(E.RECEIPT_Q, 0) > ISNULL(F.INOUT_Q, 0)
AND A.DIV_CODE = #{DIV_CODE}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND B.DVRY_DATE >= #{FR_DVRY_DATE}
AND B.DVRY_DATE <= #{TO_DVRY_DATE}
AND E.RECEIPT_NUM = #{INSPEC_NUM}
AND A.ORDER_TYPE = #{ORDER_TYPE}
AND B.WH_CODE = #{WH_CODE}
AND A.MONEY_UNIT = #{MONEY_UNIT}
ORDER BY B.ORDER_NUM DESC, B.ORDER_SEQ END
SET NOCOUNT OFF SET ARITHABORT OFF END

	SELECT ISNULL(MAX(STOCK_Q) , 0) STOCK_Q
     , ISNULL(MAX(AVERAGE_P)   , 0) AVERAGE_P
     , ISNULL(MAX(GOOD_STOCK_Q), 0) GOOD_STOCK_Q
     , ISNULL(MAX(BAD_STOCK_Q) , 0) BAD_STOCK_Q
  FROM BIV100T
 WHERE COMP_CODE = #{S_COMP_CODE}
   AND WH_CODE   = #{WH_CODE}
   AND ITEM_CODE = #{ITEM_CODE}

BEGIN
	DECLARE   @STOCK_UNIT NVARCHAR(08)

	SET NOCOUNT ON
	SET ARITHABORT ON

	SELECT TOP 1 @STOCK_UNIT = STOCK_UNIT
      FROM BPR100T WITH (NOLOCK)
     WHERE COMP_CODE = #{S_COMP_CODE}
       AND ITEM_CODE = #{ITEM_CODE}

    SELECT #{ORDER_UNIT} AS ORDER_UNIT 
	     , @STOCK_UNIT AS STOCK_UNIT
	     , #{ITEM_ACCOUNT} AS ITEM_ACCOUNT
	     , CASE #{ORDER_UNIT}
	            WHEN  @STOCK_UNIT 
	                 THEN 1
	                 ELSE ISNULL(ISNULL(ISNULL((SELECT A.TRNS_RATE
	                                              FROM BPR300T A WITH (NOLOCK)
	                                             WHERE A.TYPE            = N'1' 
	                                               AND A.CUSTOM_CODE     = #{CUSTOM_CODE}
	                                               AND A.ITEM_CODE       = #{ITEM_CODE}
	                                               AND A.ORDER_UNIT      = #{ORDER_UNIT}
	                                               AND A.COMP_CODE       = #{S_COMP_CODE}
	                                               AND A.DIV_CODE        = #{DIV_CODE}
	                                               AND A.APLY_START_DATE = (SELECT MAX(APLY_START_DATE) 
	                                                                          FROM BPR300T WITH (NOLOCK)
	                                                                         WHERE TYPE             = A.TYPE
	                                                                           AND COMP_CODE        = A.COMP_CODE
	                                                                           AND DIV_CODE         = A.DIV_CODE
	                                                                           AND ITEM_CODE        = A.ITEM_CODE
	                                                                           AND CUSTOM_CODE      = A.CUSTOM_CODE
	                                                                           AND APLY_START_DATE <= CONVERT(VARCHAR(8), GETDATE() ,112))) 
	                                   , (SELECT A.TRNS_RATE
	                                        FROM BPR200T A WITH (NOLOCK)
	                                       WHERE A.ITEM_CODE  = #{ITEM_CODE}
	                                         AND A.COMP_CODE  = #{S_COMP_CODE}
	                                         AND A.DIV_CODE   = #{DIV_CODE}
	                                         AND A.ORDER_UNIT = #{ORDER_UNIT}))
	                            , (SELECT TRNS_RATE
	                                 FROM BPR600T WITH (NOLOCK)
	                                WHERE COMP_CODE  = #{S_COMP_CODE}
	                                  AND STOCK_UNIT = @STOCK_UNIT
	                                  AND ORDER_UNIT = #{ORDER_UNIT})),1)
	        END AS TRNS_RATE
	     , ISNULL((SELECT uniLITE.fnformat(#{S_COMP_CODE}, MAX(ITEM_P), 'M_FSET_PS')
	                 FROM BPR400T WITH (NOLOCK)
	                WHERE TYPE            = N'1'
	                  AND ITEM_CODE       = #{ITEM_CODE}
	                  AND CUSTOM_CODE     = #{CUSTOM_CODE}
	                  AND MONEY_UNIT      = #{MONEY_UNIT}
	                  AND ORDER_UNIT      = #{ORDER_UNIT}
	                  AND COMP_CODE       = #{S_COMP_CODE}
	                  AND DIV_CODE        = #{DIV_CODE}
	                  AND APLY_START_DATE = (SELECT MAX(APLY_START_DATE)
	                                           FROM BPR400T WITH (NOLOCK)
	                                          WHERE APLY_START_DATE <= #{ORDER_DATE}
	                                            AND TYPE             = N'1'
	                                            AND ITEM_CODE        = #{ITEM_CODE}
	                                            AND CUSTOM_CODE      = #{CUSTOM_CODE}
	                                            AND MONEY_UNIT       = #{MONEY_UNIT}
	                                            AND COMP_CODE        = #{S_COMP_CODE}
	                                            AND DIV_CODE         = #{DIV_CODE}
	                                            AND ORDER_UNIT       = #{ORDER_UNIT}))
	     , (SELECT uniLITE.fnformat(#{S_COMP_CODE}, ISNULL(MAX(PURCHASE_BASE_P),0), 'M_FSET_PS')
	          FROM BPR200T WITH (NOLOCK)
	         WHERE COMP_CODE = #{S_COMP_CODE}
	           AND DIV_CODE  = #{DIV_CODE}
	           AND ITEM_CODE = #{ITEM_CODE})) AS ORDER_P
	     , ISNULL((SELECT uniLITE.fnformat(#{S_COMP_CODE}, BASIS_P,'M_FSET_PS')
	                 FROM BPR200T WITH (NOLOCK)
	                WHERE ITEM_CODE = #{ITEM_CODE}
	                  AND COMP_CODE = #{S_COMP_CODE}
	                  AND DIV_CODE  = #{DIV_CODE}),0) AS STOCK_P

END

