mapper.select:
/* 입고내역조회 */ /* s_otr120ukrv_jwServiceImpl.selectList2 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */ SELECT A.INOUT_NUM , A.INOUT_SEQ , A.INOUT_METH , A.INOUT_TYPE_DETAIL , A.ITEM_CODE , (CASE WHEN @RefItem = '1' THEN C1.ITEM_NAME1 WHEN @RefItem = '2' THEN C1.ITEM_NAME2 ELSE C1.ITEM_NAME END) AS ITEM_NAME , C2.ITEM_ACCOUNT , C1.SPEC , ISNULL(A.ORDER_UNIT, C1.STOCK_UNIT) AS ORDER_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_Q, 0), 'M_FSET_QS') AS ORDER_UNIT_Q , A.ITEM_STATUS , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_Q, 0), 'M_FSET_QS') AS ORIGINAL_Q , uniLITE.fnFormat(@CompCode, ISNULL(C.GOOD_STOCK_Q, 0), 'M_FSET_QS') AS GOOD_STOCK_Q , uniLITE.fnFormat(@CompCode, ISNULL(C.BAD_STOCK_Q, 0), 'M_FSET_QS') AS BAD_STOCK_Q , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q,0) - ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS NOINOUT_Q , A.PRICE_YN , A.MONEY_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_P, 0), 'M_FSET_PS') AS INOUT_FOR_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_O, 0), 'M_FSET_OS') AS INOUT_FOR_O , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_FOR_P, 0), 'M_FSET_PS') AS ORDER_UNIT_FOR_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_O, 0), 'M_FSET_OS') AS ORDER_UNIT_FOR_O , A.ACCOUNT_YNC , uniLITE.fnFormat(@CompCode, ISNULL(A.EXCHG_RATE_O, 0), 'M_FSET_RS') AS EXCHG_RATE_O , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_P, 0), 'M_FSET_PS') AS INOUT_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_I, 0), 'M_FSET_IS') AS INOUT_I , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_P, 0), 'M_FSET_PS') AS ORDER_UNIT_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_I, 0), 'M_FSET_IS') AS ORDER_UNIT_I , C1.STOCK_UNIT , A.TRNS_RATE , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_Q, 0), 'M_FSET_QS') AS INOUT_Q , A.ORDER_TYPE , A.LC_NUM , A.BL_NUM , A.ORDER_NUM , A.ORDER_SEQ , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q, 0), 'M_FSET_QS') AS ORDER_Q , A.INOUT_CODE_TYPE , A.WH_CODE , A.WH_CELL_CODE , C4.WH_CELL_NAME
, CONVERT(CHAR(8), A.INOUT_DATE, 112) AS DUMMY_INOUT_DATE -- 라벨출력시 사용 , CONVERT(CHAR(8), DATEADD(MM, 6, A.INOUT_DATE), 112) AS DUMMY_END_DATE -- 라벨출력시 사용
, (CASE WHEN ISNULL(A.INOUT_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.INOUT_DATE, 1, 4)) , 'MM' , SUBSTRING(A.INOUT_DATE, 5, 2)) , 'DD' , SUBSTRING(A.INOUT_DATE, 7, 2)) END) AS INOUT_DATE , A.INOUT_PRSN , uniLITE.fnFormat(@CompCode, ISNULL(A.ACCOUNT_Q, 0), 'M_FSET_QS') AS ACCOUNT_Q , A.CREATE_LOC , (CASE WHEN ISNULL(A.SALE_C_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.SALE_C_DATE, 1, 4)) , 'MM' , SUBSTRING(A.SALE_C_DATE, 5, 2)) , 'DD' , SUBSTRING(A.SALE_C_DATE, 7, 2)) END) AS SALE_C_DATE , A.REMARK , A.PROJECT_NO , A.LOT_NO , A.INOUT_TYPE , A.INOUT_CODE , A.DIV_CODE , (CASE WHEN @RefItem = '1' THEN C3.CUSTOM_NAME1 WHEN @RefItem = '2' THEN C3.CUSTOM_NAME2 ELSE C3.CUSTOM_NAME END) AS CUSTOM_NAME , C3.COMPANY_NUM , uniLITE.fnFormat(@CompCode, ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS INSTOCK_Q , A.SALE_DIV_CODE , A.SALE_CUSTOM_CODE , A.BILL_TYPE , A.SALE_TYPE , A.UPDATE_DB_USER , A.UPDATE_DB_TIME , uniLITE.fnFormat(@CompCode, C2.EXCESS_RATE, 'M_FSET_RS') AS EXCESS_RATE , A.INSPEC_NUM , ISNULL(A.INSPEC_SEQ,0) AS INSPEC_SEQ , A.COMP_CODE , A.BASIS_NUM , ISNULL(A.BASIS_SEQ,0) AS BASIS_SEQ , A.SCM_FLAG_YN , CASE WHEN ISNULL(C2.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(C2.PACK_QTY, 1) END AS PACK_QTY -- , ISNULL(C2.PACK_QTY, 1) AS PACK_QTY FROM BTR100T A WITH (NOLOCK) LEFT JOIN MPO200T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.ORDER_NUM = A.ORDER_NUM AND B.ORDER_SEQ = A.ORDER_SEQ LEFT JOIN BIV100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.DIV_CODE = A.DIV_CODE AND C.WH_CODE = A.WH_CODE AND C.ITEM_CODE = A.ITEM_CODE INNER JOIN BPR100T C1 WITH (NOLOCK) ON C1.COMP_CODE = A.COMP_CODE AND C1.ITEM_CODE = A.ITEM_CODE INNER JOIN BPR200T C2 WITH (NOLOCK) ON C2.COMP_CODE = A.COMP_CODE AND C2.DIV_CODE = A.DIV_CODE AND C2.ITEM_CODE = A.ITEM_CODE INNER JOIN BCM100T C3 WITH (NOLOCK) ON C3.COMP_CODE = A.COMP_CODE AND C3.CUSTOM_CODE = A.INOUT_CODE LEFT JOIN BSA225T C4 WITH (NOLOCK) ON C4.COMP_CODE = A.COMP_CODE AND C4.DIV_CODE = A.DIV_CODE AND C4.WH_CODE = A.WH_CODE AND C4.WH_CELL_CODE = A.WH_CELL_CODE WHERE A.COMP_CODE = @CompCode
AND A.DIV_CODE = #{DIV_CODE}
AND A.INOUT_NUM = #{INOUT_NUM}
AND A.INOUT_TYPE = N'1' AND A.CREATE_LOC = N'2' AND A.INOUT_CODE_TYPE = N'5' ORDER BY A.INOUT_DATE DESC, A.INOUT_NUM DESC, A.INOUT_SEQ ASC
SET NOCOUNT OFF SET ARITHABORT OFF END

/* s_otr120ukrv_jwServiceImpl.selectClipList */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) /* 명칭 참조 유형 */ , @DateFormat NVARCHAR(10) /* 날짜 포맷 유형 설정 */
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* 데이터 조회 */ SELECT A.INOUT_NUM , A.INOUT_SEQ , A.INOUT_METH , A.INOUT_TYPE_DETAIL , A.ITEM_CODE , (CASE WHEN @RefItem = '1' THEN C1.ITEM_NAME1 WHEN @RefItem = '2' THEN C1.ITEM_NAME2 ELSE C1.ITEM_NAME END) AS ITEM_NAME , C2.ITEM_ACCOUNT , C1.SPEC , ISNULL(A.ORDER_UNIT, C1.STOCK_UNIT) AS ORDER_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_Q, 0), 'M_FSET_QS') AS ORDER_UNIT_Q , A.ITEM_STATUS , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_Q, 0), 'M_FSET_QS') AS ORIGINAL_Q , uniLITE.fnFormat(@CompCode, ISNULL(C.GOOD_STOCK_Q, 0), 'M_FSET_QS') AS GOOD_STOCK_Q , uniLITE.fnFormat(@CompCode, ISNULL(C.BAD_STOCK_Q, 0), 'M_FSET_QS') AS BAD_STOCK_Q , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q,0) - ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS NOINOUT_Q , A.PRICE_YN , A.MONEY_UNIT , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_P, 0), 'M_FSET_PS') AS INOUT_FOR_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_O, 0), 'M_FSET_OS') AS INOUT_FOR_O , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_FOR_P, 0), 'M_FSET_PS') AS ORDER_UNIT_FOR_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_FOR_O, 0), 'M_FSET_OS') AS ORDER_UNIT_FOR_O , A.ACCOUNT_YNC , uniLITE.fnFormat(@CompCode, ISNULL(A.EXCHG_RATE_O, 0), 'M_FSET_RS') AS EXCHG_RATE_O , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_P, 0), 'M_FSET_PS') AS INOUT_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_I, 0), 'M_FSET_IS') AS INOUT_I , uniLITE.fnFormat(@CompCode, ISNULL(A.ORDER_UNIT_P, 0), 'M_FSET_PS') AS ORDER_UNIT_P , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_I, 0), 'M_FSET_IS') AS ORDER_UNIT_I , C1.STOCK_UNIT , A.TRNS_RATE , uniLITE.fnFormat(@CompCode, ISNULL(A.INOUT_Q, 0), 'M_FSET_QS') AS INOUT_Q , A.ORDER_TYPE , A.LC_NUM , A.BL_NUM , A.ORDER_NUM , A.ORDER_SEQ , uniLITE.fnFormat(@CompCode, ISNULL(B.ORDER_Q, 0), 'M_FSET_QS') AS ORDER_Q , A.INOUT_CODE_TYPE , A.WH_CODE , A.WH_CELL_CODE , C4.WH_CELL_NAME
, CONVERT(CHAR(8), A.INOUT_DATE, 112) AS DUMMY_INOUT_DATE -- 라벨출력시 사용 , CONVERT(CHAR(8), DATEADD(MM, 6, A.INOUT_DATE), 112) AS DUMMY_END_DATE -- 라벨출력시 사용
, (CASE WHEN ISNULL(A.INOUT_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.INOUT_DATE, 1, 4)) , 'MM' , SUBSTRING(A.INOUT_DATE, 5, 2)) , 'DD' , SUBSTRING(A.INOUT_DATE, 7, 2)) END) AS INOUT_DATE , A.INOUT_PRSN , uniLITE.fnFormat(@CompCode, ISNULL(A.ACCOUNT_Q, 0), 'M_FSET_QS') AS ACCOUNT_Q , A.CREATE_LOC , (CASE WHEN ISNULL(A.SALE_C_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.SALE_C_DATE, 1, 4)) , 'MM' , SUBSTRING(A.SALE_C_DATE, 5, 2)) , 'DD' , SUBSTRING(A.SALE_C_DATE, 7, 2)) END) AS SALE_C_DATE , A.REMARK , A.PROJECT_NO , A.LOT_NO , A.INOUT_TYPE , A.INOUT_CODE , A.DIV_CODE , (CASE WHEN @RefItem = '1' THEN C3.CUSTOM_NAME1 WHEN @RefItem = '2' THEN C3.CUSTOM_NAME2 ELSE C3.CUSTOM_NAME END) AS CUSTOM_NAME , C3.COMPANY_NUM , uniLITE.fnFormat(@CompCode, ISNULL(B.INSTOCK_Q, 0), 'M_FSET_QS') AS INSTOCK_Q , A.SALE_DIV_CODE , A.SALE_CUSTOM_CODE , A.BILL_TYPE , A.SALE_TYPE , A.UPDATE_DB_USER , A.UPDATE_DB_TIME , uniLITE.fnFormat(@CompCode, C2.EXCESS_RATE, 'M_FSET_RS') AS EXCESS_RATE , A.INSPEC_NUM , ISNULL(A.INSPEC_SEQ,0) AS INSPEC_SEQ , A.COMP_CODE , A.BASIS_NUM , ISNULL(A.BASIS_SEQ,0) AS BASIS_SEQ , A.SCM_FLAG_YN , CASE WHEN ISNULL(C2.PACK_QTY, 1) = 0 THEN 1 ELSE ISNULL(C2.PACK_QTY, 1) END AS PACK_QTY -- , ISNULL(C2.PACK_QTY, 1) AS PACK_QTY FROM BTR100T A WITH (NOLOCK) LEFT JOIN MPO200T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.ORDER_NUM = A.ORDER_NUM AND B.ORDER_SEQ = A.ORDER_SEQ LEFT JOIN BIV100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.DIV_CODE = A.DIV_CODE AND C.WH_CODE = A.WH_CODE AND C.ITEM_CODE = A.ITEM_CODE INNER JOIN BPR100T C1 WITH (NOLOCK) ON C1.COMP_CODE = A.COMP_CODE AND C1.ITEM_CODE = A.ITEM_CODE INNER JOIN BPR200T C2 WITH (NOLOCK) ON C2.COMP_CODE = A.COMP_CODE AND C2.DIV_CODE = A.DIV_CODE AND C2.ITEM_CODE = A.ITEM_CODE INNER JOIN BCM100T C3 WITH (NOLOCK) ON C3.COMP_CODE = A.COMP_CODE AND C3.CUSTOM_CODE = A.INOUT_CODE LEFT JOIN BSA225T C4 WITH (NOLOCK) ON C4.COMP_CODE = A.COMP_CODE AND C4.DIV_CODE = A.DIV_CODE AND C4.WH_CODE = A.WH_CODE AND C4.WH_CELL_CODE = A.WH_CELL_CODE WHERE A.COMP_CODE = @CompCode
AND A.DIV_CODE = #{DIV_CODE}
AND A.INOUT_NUM = #{INOUT_NUM}
AND A.INOUT_TYPE = N'1' AND A.CREATE_LOC = N'2' AND A.INOUT_CODE_TYPE = N'5' AND A.ITEM_CODE IN
#{item}
AND A.LOT_NO IN
#{item}
AND A.WH_CODE IN
#{item}
ORDER BY A.INOUT_DATE DESC, A.INOUT_NUM DESC, A.INOUT_SEQ ASC
