	multiply:function (a, b) {
    // get number of decimal places to shift
    	var sA = a.toString(), sB=b.toString();
    	if(sA.indexOf(".") == -1 && sB.indexOf(".") == -1)	{
    		return (a*b);	
    	}
    	
    	var aDp = (sA.indexOf(".") == -1) ? sA.length : sA.indexOf("."), 
    		bDp = (sB.indexOf(".") == -1) ? sB.length : sB.indexOf(".");
    	
	    var exp = 1;
	   
	    if( (sA.length-aDp) - (sB.length-bDp) > 0) {
	    	exp = (sA.length-aDp)+1 ;
		}else {
	    	exp = (sB.length-bDp)+1 ;
	    }

	   function makeInt (num) {
	       var value = Math.round(num * Math.pow(10, exp));
	       return value > 0 ? Math.floor(value) : Math.ceil(value);
	   }
	
	   function makeFloat(num) {
	       return num / Math.pow(100, exp);
	   }
	
	   return makeFloat(makeInt(a) * makeInt(b));
	},	
