	        var sorters = [];
	 		if(grid.uniSortingToolbar ) {
		        Ext.each(grid.uniSortingToolbar.query('segmentedbutton'), function(button) {
		            sorters.push(button.sortData);
		        }, this);
	 		}
	        return sorters;
