	charlen : function(str){
		var charLength = 0;
		var ch1 = "";
		var strCapital ="ABCDEFGHIJKLMNOPQRSTUVWXYZ";
		if(str)	{
			for(var i=0; i < str.length ;i++)	{
				ch1 = str.charAt(i);
				if(escape(ch1).length > 4)	{
					charLength +=2;
				} else {
					if(strCapital.indexOf(ch1) > -1)	{
						charLength +=2;
					} else {
						charLength +=1;
					}
				}
			}
		}
		return charLength;
	},
