	_onSpecialkey:function(field, event, eOpts){
		var me = this;
		
    	if(event.getKey() == 13)	{
			var upEl = field.up();
			var upElXtype = (upEl && upEl.xtype) ? upEl.xtype: "";  // Grid가 formpanel 내부에 구성된 경우 체크
			
			if( upElXtype == "celleditor" || upElXtype == "editor") { 
				var grid = field.up('grid');
				var view = grid.getView();
                var position;
				upEl.completeEdit();
				event.stopEvent();
				
				view.setActionableMode(false, view.actionPosition);
			
			}else if( event.shiftKey && !event.ctrlKey && !event.altKey) {
				Unilite.focusPrevField(field, true, event);
	    	}else if( !event.shiftKey && !event.ctrlKey && !event.altKey){
	    		Unilite.focusNextField(field, true, event);
	    	}
    	}
    	
    	if(event.getKey() == 39)	{
			var picker = me.getPicker();
    		picker.select(0);
    	}
		
	},
