	 , DR_ACCNT       = #{DR_ACCNT}
	 , CR_ACCNT       = #{CR_ACCNT}
	 , DEPT_CODE      = #{DEPT_CODE}
	 , DIV_CODE       = #{DIV_CODE}
	 , AC_DATE        = #{PUB_DATE}
	 , SLIP_NUM       = #{SLIP_NUM}
	 , UPDATE_DB_USER = #{S_USER_ID}
	 , UPDATE_DB_TIME = GETDATE()
 WHERE COMP_CODE = #{S_COMP_CODE}
   AND APPR_DATE = #{PUB_DATE}
   AND APPR_TIME = #{APPR_TIME}
   AND APPR_NO   = #{APPR_NO}     --20210728 추가
   AND REPLACE(uniLITE.fnCipherDecrypt(CRDT_FULL_NUM,''),'-','') =  REPLACE(uniLITE.fnCipherDecrypt(#{CREDIT_NUM},''),'-','')
   AND CANCEL_YN = #{CANCEL_YN}

mapper.update:
