mapper.select:
/* bpr250ukrvService.selectList */ /*bpr250ukrv.Cbpr250ukrv[fnBpr251QStd] Query01 */ BEGIN SELECT A.* FROM ( SELECT #{DIV_CODE} AS DIV_CODE , A.ITEM_CODE , A.ITEM_NAME , A.ITEM_NAME1 , A.ITEM_NAME2 , A.SPEC , CASE WHEN ISNULL(B.COMP_CODE, '') = '' THEN '2' ELSE '1' END AS SFLAG , CASE WHEN ISNULL(C1.COMP_CODE, '') = '' THEN '2' ELSE '1' END AS BCNT , CASE WHEN ISNULL(C2.COMP_CODE, '') = '' THEN '2' ELSE '1' END AS BCNT1 , CASE WHEN ISNULL(C3.COMP_CODE, '') = '' THEN '2' ELSE '1' END AS BCNT2 , A.STOCK_UNIT , A.ITEM_LEVEL1 , C5.LEVEL_NAME AS ITEM_LEVEL_NAME1 , A.ITEM_LEVEL2 , C6.LEVEL_NAME AS ITEM_LEVEL_NAME2 , A.ITEM_LEVEL3 , C7.LEVEL_NAME AS ITEM_LEVEL_NAME3 , B.ITEM_ACCOUNT , B.SUPPLY_TYPE , B.ITEM_GUBUN , B.ORDER_UNIT , ISNULL(B.TRNS_RATE , 1 ) AS TRNS_RATE , B.WH_CODE , B.LOCATION , B.ORDER_PLAN , B.MATRL_PRESENT_DAY , B.PURCHASE_BASE_P , B.ORDER_PRSN , B.ABC_FLAG , ISNULL(B.LOT_YN , 'N') AS LOT_YN , ISNULL(B.PHANTOM_YN , 'N') AS PHANTOM_YN , ISNULL(B.EXCESS_RATE , 0 ) AS EXCESS_RATE , ISNULL(B.EXPENSE_RATE , 0 ) AS EXPENSE_RATE , ISNULL(B.ORDER_KIND , 'Y') AS ORDER_KIND , ISNULL(B.NEED_Q_PRESENT , 'Y') AS NEED_Q_PRESENT , ISNULL(B.EXC_STOCK_CHECK_YN, 'Y') AS EXC_STOCK_CHECK_YN , ISNULL(B.SAFE_STOCK_Q , 0 ) AS SAFE_STOCK_Q , ISNULL(B.NEED_Q_PRESENT_Q , 0 ) AS NEED_Q_PRESENT_Q , ISNULL(B.PURCH_LDTIME , 1 ) AS PURCH_LDTIME , ISNULL(B.MINI_PURCH_Q , 0 ) AS MINI_PURCH_Q , ISNULL(B.MAX_PURCH_Q , 0 ) AS MAX_PURCH_Q , B.CUSTOM_CODE , C4.CUSTOM_NAME , B.TEMPC_01 , ISNULL(B.ROP_YN , 'N') AS ROP_YN , ISNULL(B.DAY_AVG_SPEND , 0 ) AS DAY_AVG_SPEND , ISNULL(B.ORDER_POINT , 0 ) AS ORDER_POINT , ISNULL(B.BASIS_P , 0 ) AS BASIS_P , ISNULL(B.COST_YN , 'Y') AS COST_YN , B.COST_PRICE , ISNULL(B.REAL_CARE_YN , 'Y') AS REAL_CARE_YN , B.REAL_CARE_PERIOD , ISNULL(B.MINI_PACK_Q, 1) AS MINI_PACK_Q , ISNULL(B.ORDER_METH, '3') AS ORDER_METH , B.OUT_METH , B.RESULT_YN , ISNULL(B.PRODUCT_LDTIME , 1 ) AS PRODUCT_LDTIME , ISNULL(B.MAX_PRODT_Q , 0 ) AS MAX_PRODT_Q , ISNULL(B.STAN_PRODT_Q , 0 ) AS STAN_PRODT_Q , B.ROUT_TYPE , B.WORK_SHOP_CODE , B.ITEM_TYPE /* 양산구분(B074)-2008/04/07 ADD By Yang.J.Y */ , B.ITEM_CODE AS ITEM_CODE2 , A.UPDATE_DB_USER , A.UPDATE_DB_TIME , ISNULL(B.DIST_LDTIME , 1 ) AS DIST_LDTIME , ISNULL(B.ATP_LDTIME , 1 ) AS ATP_LDTIME , ISNULL(B.INSPEC_YN , 'Y') AS INSPEC_YN , ISNULL(B.BAD_RATE , 0 ) AS BAD_RATE , B.INSPEC_METH_MATRL , B.INSPEC_METH_PROG , B.INSPEC_METH_PRODT , ISNULL(B.DISTR_TYPE ,'') AS DISTR_TYPE , ISNULL(B.COST_REF_YN ,'') AS COST_REF_YN , ISNULL(B.CP_DISTR_YN ,'') AS CP_DISTR_YN , ISNULL(B.LAST_PRODT_YN ,'') AS LAST_PRODT_YN , ISNULL(B.LLC_COST_TYPE ,'') AS LLC_COST_TYPE , A.COMP_CODE , B.ITEM_ACCOUNT AS ITEM_ACCOUNT_ORG , B.MAN_HOUR AS MAN_HOUR --20190305 추가 , ISNULL(B.CARE_YN, 'N') AS CARE_YN , B.CARE_REASON FROM BPR100T A WITH (NOLOCK) LEFT JOIN BPR200T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = #{DIV_CODE} AND B.ITEM_CODE = A.ITEM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, PROD_ITEM_CODE FROM BPR500T WITH (NOLOCK) WHERE COMP_CODE = #{S_COMP_CODE} AND DIV_CODE = #{DIV_CODE} AND CHILD_ITEM_CODE != '$' GROUP BY COMP_CODE, DIV_CODE, PROD_ITEM_CODE ) C1 ON C1.COMP_CODE = B.COMP_CODE AND C1.DIV_CODE = B.DIV_CODE AND C1.PROD_ITEM_CODE = B.ITEM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, ITEM_CODE FROM BPR400T WITH (NOLOCK) WHERE COMP_CODE = #{S_COMP_CODE} AND DIV_CODE = #{DIV_CODE} AND TYPE = '1' /* 거래처별 구매단가 */ GROUP BY COMP_CODE, DIV_CODE, ITEM_CODE ) C2 ON C2.COMP_CODE = B.COMP_CODE AND C2.DIV_CODE = B.DIV_CODE AND C2.ITEM_CODE = B.ITEM_CODE LEFT JOIN ( SELECT COMP_CODE, DIV_CODE, ITEM_CODE FROM BPR400T WITH (NOLOCK) WHERE COMP_CODE = #{S_COMP_CODE} AND DIV_CODE = #{DIV_CODE} AND TYPE = '2' /* 거래처별 판매단가 */ GROUP BY COMP_CODE, DIV_CODE, ITEM_CODE ) C3 ON C3.COMP_CODE = B.COMP_CODE AND C3.DIV_CODE = B.DIV_CODE AND C3.ITEM_CODE = B.ITEM_CODE LEFT JOIN BCM100T C4 WITH (NOLOCK) ON C4.COMP_CODE = B.COMP_CODE AND C4.CUSTOM_CODE = B.CUSTOM_CODE LEFT JOIN BPR000T C5 WITH (NOLOCK) ON C5.COMP_CODE = A.COMP_CODE AND C5.LEVEL1 != '*' AND C5.LEVEL1 = A.ITEM_LEVEL1 AND C5.LEVEL2 = '*' AND C5.LEVEL3 = '*' LEFT JOIN BPR000T C6 WITH (NOLOCK) ON C6.COMP_CODE = A.COMP_CODE AND C6.LEVEL1 != '*' AND C6.LEVEL1 = A.ITEM_LEVEL1 AND C6.LEVEL2 != '*' AND C6.LEVEL2 = A.ITEM_LEVEL2 AND C6.LEVEL3 = '*' LEFT JOIN BPR000T C7 WITH (NOLOCK) ON C7.COMP_CODE = A.COMP_CODE AND C7.LEVEL1 != '*' AND C7.LEVEL1 = A.ITEM_LEVEL1 AND C7.LEVEL2 != '*' AND C7.LEVEL2 = A.ITEM_LEVEL2 AND C7.LEVEL3 != '*' AND C7.LEVEL3 = A.ITEM_LEVEL3 LEFT JOIN BSA100T C8 WITH (NOLOCK) ON C8.COMP_CODE = A.COMP_CODE AND C8.MAIN_CODE = 'B914' AND C8.SUB_CODE = '01' WHERE A.COMP_CODE = #{S_COMP_CODE}
AND B.ITEM_ACCOUNT = #{ITEM_ACCOUNT}
AND ( ((CASE WHEN ISNULL(C8.REF_CODE1, '') = '' THEN '3' ELSE C8.REF_CODE1 END) = '1' AND A.ITEM_CODE LIKE '%' + #{ITEM_CODE}) OR ((CASE WHEN ISNULL(C8.REF_CODE1, '') = '' THEN '3' ELSE C8.REF_CODE1 END) = '2' AND A.ITEM_CODE LIKE #{ITEM_CODE} + '%') OR ((CASE WHEN ISNULL(C8.REF_CODE1, '') = '' THEN '3' ELSE C8.REF_CODE1 END) = '3' AND A.ITEM_CODE LIKE '%' + #{ITEM_CODE} + '%') )
AND A.ITEM_NAME LIKE '%' + #{ITEM_NAME} + '%'
AND A.${FIND_TYPE} LIKE '%' + #{FIND_TXT} + '%'
AND B.SUPPLY_TYPE = #{SUPPLY_TYPE}
AND B.ITEM_GUBUN = #{ITEM_GUBUN}
AND B.ORDER_PRSN = #{ORDER_PRSN}
AND ISNULL(B.PURCH_LDTIME, 0) > 0
AND ISNULL(B.PURCH_LDTIME, 0) <= 0
AND B.ROUT_TYPE = #{ROUT_TYPE}
AND B.WORK_SHOP_CODE = #{WORK_SHOP_CODE}
AND B.CUSTOM_CODE LIKE #{CUSTOM_CODE} + '%'
AND C4.CUSTOM_NAME LIKE '%' + #{CUSTOM_NAME} + '%'
AND A.ITEM_LEVEL1 = #{ITEM_LEVEL1}
AND A.ITEM_LEVEL2 = #{ITEM_LEVEL2}
AND A.ITEM_LEVEL3 = #{ITEM_LEVEL3}
) AS A WHERE A.COMP_CODE = #{S_COMP_CODE}
AND A.SFLAG = #{SFLAG}
AND A.BCNT = #{BCNT}
AND A.BCNT1 = #{BCNT1}
AND A.BCNT2 = #{BCNT2}
ORDER BY A.ITEM_CODE END

SELECT REF_CODE1
FROM    BSA100T WITH (NOLOCK)
WHERE COMP_CODE	= #{S_COMP_CODE}
  AND MAIN_CODE='B052'
  AND SUB_CODE=#{FIND_TYPE}

SELECT COUNT(ITEM_CODE) AS CNT
FROM   BPR200T WITH (NOLOCK)
WHERE  COMP_CODE=#{COMP_CODE}
  AND  DIV_CODE=#{DIV_CODE}
  AND  ITEM_CODE=#{ITEM_CODE}

/* bpr250ukrvService.checkChildCode */
SELECT PROD_ITEM_CODE
     , CHILD_ITEM_CODE
  FROM BPR500T WITH (NOLOCK)
 WHERE COMP_CODE       = #{COMP_CODE}
   AND CHILD_ITEM_CODE = #{ITEM_CODE}

/* bpr250ukrvService.checkExnum */ /* bpr250ukrv.Cbpr250ukrv[fnBpr250Save] Query03 */
SELECT T.ITEM_CODE, T.EX_DATE, T.EX_NUM FROM ( /* 매입전표 */ SELECT TOP 1 B.ITEM_CODE, C.EX_DATE, C.EX_NUM FROM MAP100T AS A WITH (NOLOCK) INNER JOIN MAP200T AS B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.CHANGE_BASIS_NUM = A.CHANGE_BASIS_NUM LEFT JOIN AGJ110T AS C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.EX_DATE = A.EX_DATE AND C.EX_NUM = A.EX_NUM WHERE A.COMP_CODE = #{S_COMP_CODE} AND A.DIV_CODE = #{DIV_CODE} AND ISNULL(C.MOD_DIVI,'') != 'D' /* 삭제된 전표 아닌것만 */ AND ISNULL(C.EX_DATE,'') != '' /* 전표번호가 있으면 수정 불가능 */ AND B.ITEM_CODE = #{ITEM_CODE}
UNION ALL
/* 매출기표 */ SELECT TOP 1 B.ITEM_CODE, C.EX_DATE, C.EX_NUM FROM SSA100T AS A WITH (NOLOCK) INNER JOIN SSA110T AS B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.BILL_NUM = A.BILL_NUM LEFT JOIN AGJ110T AS C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.EX_DATE = A.EX_DATE AND C.EX_NUM = A.EX_NUM WHERE A.COMP_CODE = #{S_COMP_CODE} AND A.DIV_CODE = #{DIV_CODE} AND ISNULL(C.MOD_DIVI,'') != 'D' /* 삭제된 전표 아닌것만 */ AND ISNULL(C.EX_DATE,'') != '' /* 전표번호가 있으면 수정 불가능 */ AND B.ITEM_CODE = #{ITEM_CODE}
UNION ALL
/* 세금계산서 매출기표 */ SELECT TOP 1 B.ITEM_CODE, C.EX_DATE, C.EX_NUM FROM SSA100T AS A WITH (NOLOCK) INNER JOIN SSA110T AS B WITH (NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DIV_CODE = B.DIV_CODE AND A.BILL_NUM = B.BILL_NUM INNER JOIN STB100T AS C WITH (NOLOCK) ON B.COMP_CODE = C.COMP_CODE AND B.TO_DIV_CODE = C.DIV_CODE AND B.PUB_NUM = C.PUB_NUM LEFT JOIN AGJ110T AS D WITH (NOLOCK) ON D.COMP_CODE = C.COMP_CODE AND D.EX_DATE = C.EX_DATE AND D.EX_NUM = C.EX_NUM WHERE A.COMP_CODE = #{S_COMP_CODE} AND A.DIV_CODE = #{DIV_CODE} AND ISNULL(D.MOD_DIVI,'') != 'D' /* 삭제된 전표 아닌것만 */ AND ISNULL(D.EX_DATE,'') != '' /* 전표번호가 있으면 수정 불가능 */ AND B.ITEM_CODE = #{ITEM_CODE}
UNION ALL
/* 수출 선적기표 */ SELECT TOP 1 B.ITEM_CODE, C.EX_DATE, C.EX_NUM FROM TED100T AS A WITH (NOLOCK) INNER JOIN TED110T AS B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.BL_SER_NO = A.BL_SER_NO LEFT JOIN AGJ110T AS C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.EX_DATE = A.EX_DATE AND C.EX_NUM = A.EX_NUM WHERE A.COMP_CODE = #{S_COMP_CODE} AND A.DIV_CODE = #{DIV_CODE} AND ISNULL(C.MOD_DIVI,'') != 'D' /* 삭제된 전표 아닌것만 */ AND ISNULL(C.EX_DATE,'') != '' /* 전표번호가 있으면 수정 불가능 */ AND B.ITEM_CODE = #{ITEM_CODE}
UNION ALL
/* 수출 LOCAL 매출기표 */ SELECT TOP 1 B.ITEM_CODE, C.EX_DATE, C.EX_NUM FROM TEC120T AS A WITH (NOLOCK) INNER JOIN TEC130T AS B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.ACCEPT_NO = A.ACCEPT_NO LEFT JOIN AGJ110T AS C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.EX_DATE = A.EX_DATE AND C.EX_NUM = A.EX_NUM WHERE A.COMP_CODE = #{S_COMP_CODE} AND A.DIV_CODE = #{DIV_CODE} AND ISNULL(C.MOD_DIVI,'') != 'D' /* 삭제된 전표 아닌것만 */ AND ISNULL(C.EX_DATE,'') != '' /* 전표번호가 있으면 수정 불가능 */ AND B.ITEM_CODE = #{ITEM_CODE}
) AS T

