/* hat500ukrServiceImpl.getDutyRule */
SELECT DUTY_INPUT_RULE
  FROM HBS400T WITH(NOLOCK)
 WHERE COMP_CODE = #{S_COMP_CODE}

	/* hat500ukrServiceImpl.getDutycodeTime */
	SELECT
		   CASE WHEN A.DUTY_FR_D = 1 THEN CONVERT(NVARCHAR(8), DATEADD(DAY, -1, CONVERT(DATETIME, #{DUTY_YYYYMMDD}, 112)), 112)
		        WHEN A.DUTY_FR_D = 3 THEN CONVERT(NVARCHAR(8), DATEADD(DAY,  1, CONVERT(DATETIME, #{DUTY_YYYYMMDD}, 112)), 112)
				ELSE #{DUTY_YYYYMMDD}
		    END AS DUTY_FR_D
    	 , RIGHT('00'+CONVERT(NVARCHAR(2), A.DUTY_FR_H), 2) AS DUTY_FR_H
	     , RIGHT('00'+CONVERT(NVARCHAR(2), A.DUTY_FR_M), 2) AS DUTY_FR_M
	     , CASE WHEN A.DUTY_TO_D = 1 THEN CONVERT(NVARCHAR(8), DATEADD(DAY, -1, CONVERT(DATETIME, #{DUTY_YYYYMMDD}, 112)), 112)
		        WHEN A.DUTY_TO_D = 3 THEN CONVERT(NVARCHAR(8), DATEADD(DAY,  1, CONVERT(DATETIME, #{DUTY_YYYYMMDD}, 112)), 112)
				ELSE #{DUTY_YYYYMMDD}
		    END AS DUTY_TO_D
	     , RIGHT('00'+CONVERT(NVARCHAR(2), A.DUTY_TO_H), 2) AS DUTY_TO_H
	     , RIGHT('00'+CONVERT(NVARCHAR(2), A.DUTY_TO_M), 2) AS DUTY_TO_M
	  FROM       HAT100T A WITH(NOLOCK)
	  INNER JOIN HBS600T B WITH (NOLOCK)  ON B.CAL_DATE = #{DUTY_YYYYMMDD}
										 AND B.HOLY_TYPE = A.HOLY_TYPE
	 WHERE A.COMP_CODE = #{S_COMP_CODE}
	   AND A.WORK_TEAM = #{WORK_TEAM}

/* hat500ukrServiceImpl.getAllDutycode */ SELECT DISTINCT B.SUB_CODE , B.CODE_NAME FROM HBS100T A WITH(NOLOCK) INNER JOIN BSA100T B WITH(NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DUTY_CODE = B.SUB_CODE WHERE A.COMP_CODE = #{S_COMP_CODE} AND B.MAIN_CODE = 'H033' AND B.SUB_CODE <> '$'
AND A.COTR_TYPE = '2'
AND A.COTR_TYPE = '1'

/* hat500ukrServiceImpl.getDutyCode */ SELECT DISTINCT B.SUB_CODE , B.CODE_NAME FROM HBS100T A INNER JOIN BSA100T B ON A.COMP_CODE = B.COMP_CODE AND A.DUTY_CODE = B.SUB_CODE WHERE A.COMP_CODE = #{S_COMP_CODE} AND B.MAIN_CODE = 'H033' AND B.SUB_CODE <> '$'
AND A.PAY_CODE = #{PAY_CODE}
AND A.COTR_TYPE = '2'
AND A.COTR_TYPE = '1'
ORDER BY B.SUB_CODE

/* hat510ukrServiceImpl.getComboList2 */ SELECT B.SUB_CODE as value , B.CODE_NAME as text FROM HBS100T A WITH(NOLOCK) INNER JOIN BSA100T B WITH(NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.DUTY_CODE = B.SUB_CODE WHERE A.COMP_CODE = #{S_COMP_CODE} AND B.MAIN_CODE = 'H033' AND B.USE_YN = 'Y' AND B.SUB_CODE <> '$'
AND A.PAY_CODE = #{PAY_CODE}
AND A.PAY_CODE = '1'
AND A.COTR_TYPE = '2' ORDER BY B.REF_CODE2, B.SUB_CODE

