--20200219 추가: 수정사유(MODI_REASON), 마감여부(CLOSE_YN) 추가 , '' AS MODI_REASON , 'N' AS CLOSE_YN , 'Y' AS HIDDEN_FLAG FROM HUM100T A WITH (NOLOCK) LEFT JOIN HAT400T B WITH (NOLOCK) ON B.COMP_CODE = A.COMP_CODE AND B.PERSON_NUMB = A.PERSON_NUMB AND B.DUTY_YYYYMMDD = #{FR_DATE} LEFT JOIN HAT600T D WITH (NOLOCK) ON D.COMP_CODE = A.COMP_CODE AND D.PERSON_NUMB = A.PERSON_NUMB INNER JOIN HAT800T E WITH (NOLOCK) ON E.COMP_CODE = A.COMP_CODE AND E.PERSON_NUMB = A.PERSON_NUMB AND E.DUTY_YYYYMMDD = #{FR_DATE} LEFT JOIN BSA100T M1 WITH (NOLOCK) ON M1.COMP_CODE = A.COMP_CODE AND M1.MAIN_CODE = N'H024' AND M1.SUB_CODE = A.EMPLOY_TYPE LEFT JOIN BSA100T M2 WITH (NOLOCK) ON M2.COMP_CODE = M1.COMP_CODE AND M2.MAIN_CODE = N'H181' AND M2.SUB_CODE = M1.REF_CODE1 WHERE A.COMP_CODE = #{S_COMP_CODE} AND uniLITE.fnCheckCompDivAuth(A.COMP_CODE, A.DIV_CODE, #{S_USER_ID}) = 'Y' AND A.JOIN_DATE <= #{FR_DATE} AND (A.RETR_DATE = '00000000' OR A.RETR_DATE >= #{FR_DATE})
AND A.PAY_CODE = #{PAY_CODE}
AND D.COMP_CODE IS NULL
AND A.DIV_CODE = #{DIV_CODE}
AND A.DEPT_CODE IN
#{item}
AND ISNULL(B.WORK_TEAM, '1') = #{WORK_TEAM}
AND A.PAY_PROV_FLAG = #{PAY_PROV_FLAG}
AND A.PAY_GUBUN = #{PAY_GUBUN}
AND A.PERSON_NUMB = #{PERSON_NUMB}
AND A.EMPLOY_TYPE = #{EMPLOY_TYPE}
AND M2.SUB_CODE = #{SUB_CODE}
ORDER BY WORK_TEAM, DIV_CODE, DEPT_CODE, POST_CODE, NAME, PERSON_NUMB

