mapper.select:
 /* ssa700ukrvServiceImpl.getErrMsg    */
    SELECT TOP 1 ISNULL(ERR_MESG, '') AS ERR_MESG
	  FROM BILL_DB.BILL.BILL_LOG
	 WHERE BILLSEQ = #{BILLSEQ}
	 ORDER BY ERR_DATE DESC

/* ssa700ukrvServiceImpl.selectSendBillMaster */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) , @DateFormat NVARCHAR(10)
DECLARE @BillTypeYn NVARCHAR(01) , @ETaxDBUser NVARCHAR(50)
SET @CompCode = N'MASTER' SET @UserId = N'UNILITE5' SET @LangType = N'KR'
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* BILL_TYPE 참조시 S024의 REF_CODE1의 증빙유형 사용 여부 */ SELECT @BillTypeYn = (CASE WHEN ISNULL(REF_CODE3 , '') = '' THEN 'N' ELSE REF_CODE3 END) , @ETaxDBUser = (CASE WHEN ISNULL(REF_CODE10, '') = '' THEN 'BILL' ELSE REF_CODE10 END) FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = 'S084' AND SUB_CODE = '01'
SET @BillTypeYn = ISNULL(@BillTypeYn,'N') SET @BillTypeYn = (CASE WHEN @BillTypeYn = 'Y' THEN 'Y' ELSE 'N' END)
SET @ETaxDBUser = ISNULL(@ETaxDBUser,'BILL')
/* 데이터 조회 */ SELECT CAST('' AS BIT) AS CHK , (CASE WHEN F.TRANSYN = 'E' THEN 'Error' ELSE M2.CODE_NAME END) AS TRANSYN_NAME /* 전송여부명 */ , M3.CODE_NAME AS STS /* 상태 */ , ISNULL(F.REPORT_STAT, 'N') AS REPORT_STAT /* 국세청신고상태 */ , CONVERT(NVARCHAR, A.CRT_LOC) AS CRT_LOC /* 생성경로 */ , A.BILL_FLAG AS BILL_FLAG /* 세금계산서구분 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN '0' + A.BILL_GUBUN WHEN 'Y' THEN '0' + F.GUBUN END) AS GUBUN /* 영수/청구 구분 */ , (CASE WHEN ISNULL(A.BILL_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.BILL_DATE, 1, 4)) , 'MM' , SUBSTRING(A.BILL_DATE, 5, 2)) , 'DD' , SUBSTRING(A.BILL_DATE, 7, 2)) END) AS DT /* 발행일 */ , A.CUSTOM_CODE AS CUSTOM_CODE /* 고객코드 */ , (CASE WHEN ISNULL(A.BILL_SEND_YN, 'N') = 'Y' THEN F.RCOMPANY ELSE C.CUSTOM_FULL_NAME END) AS RCOMPANY /* 고객명 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN (CASE WHEN C.BUSINESS_TYPE = '3' THEN unilite.fnCipherDecrypt(C.TOP_NUM, 'A') ELSE SUBSTRING(C.COMPANY_NUM,1,3) + '-' + SUBSTRING(C.COMPANY_NUM,4,2) + '-' + SUBSTRING(C.COMPANY_NUM,6,5) END) WHEN 'Y' THEN (CASE WHEN C.BUSINESS_TYPE = '3' THEN F.RVENDERNO ELSE SUBSTRING(F.RVENDERNO ,1,3) + '-' + SUBSTRING(F.RVENDERNO ,4,2) + '-' + SUBSTRING(F.RVENDERNO,6,5) END) END) AS RVENDERNO /* 공급받는자 사업자번호 */ , ISNULL(A.SERVANT_COMPANY_NUM, '') AS RREG_ID /* 공급받는자 종사업장번호 */ , M1.CODE_NAME AS BILLTYPENAME /* 계산서유형 */ , A.SALE_LOC_AMT_I AS SUPMONEY /* 공급가액 */ , A.TAX_AMT_O AS TAXMONEY /* 세액 */ , ISNULL(A.SALE_LOC_AMT_I, 0) + ISNULL(A.TAX_AMT_O, 0) AS TOT_AMT_I /* 합계 */ , B.SEND_LOG_TIME AS SEND_DATE /* 전송일시 */ , A.PUB_NUM AS BILLSEQ /* 계산서번호 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN (CASE WHEN ISNULL(C4.PRSN_NAME, '') = '' THEN C.BILL_PRSN ELSE C4.PRSN_NAME END) WHEN 'Y' THEN F.RUSER END) AS BILLPRSN /* 전자문서담당자 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN (CASE WHEN ISNULL(C4.PRSN_NAME, '') = '' THEN ISNULL(C.HAND_PHON, C.TELEPHON) ELSE ISNULL(C4.HAND_PHON, C4.TELEPHONE_NUM1) END) WHEN 'Y' THEN F.RTELNO END) AS HANDPHON /* 전자문서핸드폰 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN (CASE WHEN ISNULL(C4.PRSN_NAME, '') = '' THEN ISNULL(C.BILL_MAIL_ID, '') ELSE ISNULL(C4.MAIL_ID, '') END) WHEN 'Y' THEN F.REMAIL END) AS REMAIL /* 전자문서E-MAIL */ , A.EB_NUM AS EB_NUM /* 전자문서번호 */ , ISNULL(A.MODI_REASON, '') AS REPORT_AMEND_CD /* 신고문서 수정사유코드 */ , ISNULL(A.REMARK, '') AS BIGO /* 비고 */ , ISNULL(F.REPORT_ISSUE_ID, '') AS REPORT_ISSUE_ID /* 신고문서 고유코드 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN 'N' WHEN 'Y' THEN F.REPORT_EXCEPT_YN END) AS REPORT_EXCEPT_YN /* 국세청신고제외여부 */ , 'N' AS TRANSYN /* 전송 */ , (CASE WHEN A.BILL_TYPE = '11' THEN '0' WHEN A.BILL_TYPE = '12' THEN '1' WHEN A.BILL_TYPE = '20' THEN '2' ELSE '0' END) AS TAXRATE /* 세율구분 */ , (CASE WHEN C.BUSINESS_TYPE = '3' THEN '61' ELSE (CASE WHEN C.BILL_MEM_TYPE = '1' AND (A.BILL_TYPE = '11' OR A.BILL_TYPE = '12') THEN '10' WHEN C.BILL_MEM_TYPE = '2' AND (A.BILL_TYPE = '11' OR A.BILL_TYPE = '12') THEN '11' WHEN C.BILL_MEM_TYPE = '1' AND A.BILL_TYPE = '20' THEN '20' WHEN C.BILL_MEM_TYPE = '2' AND A.BILL_TYPE = '20' THEN '21' WHEN ISNULL(C.BILL_MEM_TYPE, '') = '' AND A.BILL_TYPE = '20' THEN '21' WHEN C.BILL_MEM_TYPE = '3' AND A.BILL_TYPE = '11' THEN '40' WHEN C.BILL_MEM_TYPE = '3' AND A.BILL_TYPE = '20' THEN '50' WHEN C.BILL_MEM_TYPE = '4' AND A.BILL_TYPE = '11' THEN '61' ELSE '11' END) END) AS BILLTYPE , 0 AS CASH /* 세금계산서상의 현금지급액*/ , 0 AS CHECKS /* 세금계산서상의 수표지급액*/ , 0 AS NOTE /* 세금계산서상의 어음지급액*/ , 0 AS CREDIT /* 세금계산서상의 외상미수금*/ , C1.COMPANY_NUM AS SVENDERNO /* 공급자 사업자번호 */ , (CASE WHEN ISNULL(C1.DIV_FULL_NAME, '') = '' THEN C1.DIV_NAME ELSE C1.DIV_FULL_NAME END) AS SCOMPANY /* 공급자 업체명 */ , C1.SUB_DIV_NUM AS SREG_ID /* 공급자 종사업장번호 */ , C1.REPRE_NAME AS SCEONAME /* 공급자 대표자명 */ , C1.COMP_TYPE AS SUPTAE /* 공급자 업태 */ , C1.COMP_CLASS AS SUPJONG /* 공급자 업종 */ , C1.ADDR AS SADDRESS /* 공급자 주소 */ , '' AS SADDRESS2 /* 공급자 상세주소 */ , (CASE WHEN ISNULL(E1.CODE_NAME, '') != '' THEN E1.CODE_NAME ELSE (CASE WHEN ISNULL(E2.CODE_NAME, '') != '' THEN E2.CODE_NAME ELSE C1.DIV_NAME END)
END) AS SUSER /* 공급자 담당자명 */ , (CASE WHEN ISNULL(E3.TREE_NAME, '') != '' THEN E3.TREE_NAME ELSE (CASE WHEN ISNULL(E4.TREE_NAME, '') != '' THEN E4.TREE_NAME ELSE '' END)
END) AS SDIVISION /* 공급자 부서명 */ , (CASE WHEN ISNULL(E1.REF_CODE6, '') != '' THEN E1.REF_CODE6 ELSE (CASE WHEN ISNULL(E2.REF_CODE6, '') != '' THEN E2.REF_CODE6 ELSE C1.TELEPHON END)
END) AS STELNO /* 공급자 전화번호 */ , (CASE WHEN ISNULL(E1.REF_CODE7, '') != '' THEN E1.REF_CODE7 ELSE (CASE WHEN ISNULL(E2.REF_CODE7, '') != '' THEN E2.REF_CODE7 ELSE C2.EMAIL END)
END) AS SEMAIL /* 공급자 이메일주소 */ , (CASE WHEN ISNULL(A.BILL_SEND_YN, 'N') = 'Y' THEN F.RCEONAME ELSE C.TOP_NAME END) AS RCEONAME /* 공급받는자 대표자명 */ , (CASE WHEN ISNULL(A.BILL_SEND_YN, 'N') = 'Y' THEN F.RUPTAE ELSE C.COMP_TYPE END) AS RUPTAE /* 공급받는자 업태 */ , (CASE WHEN ISNULL(A.BILL_SEND_YN, 'N') = 'Y' THEN F.RUPJONG ELSE C.COMP_CLASS END) AS RUPJONG /* 공급받는자 업종 */ , (CASE WHEN ISNULL(A.BILL_SEND_YN, 'N') = 'Y' THEN F.RADDRESS ELSE ISNULL(C.ADDR1, '') + ISNULL(C.ADDR2, '') END) AS RADDRESS /* 공급받는자 주소 */ , '' AS RADDRESS2 /* 공급받는자 상세주소 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN (CASE WHEN ISNULL(C4.PRSN_NAME, '') = '' THEN C.BILL_PRSN ELSE C4.PRSN_NAME END) WHEN 'Y' THEN F.RUSER END) AS RUSER /* 공급받는자 담당자명 또는 거래처명 */ , '' AS RDIVISION /* 공급받는자 부서명 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN (CASE WHEN ISNULL(C4.PRSN_NAME, '') = '' THEN ISNULL(C.HAND_PHON, C.TELEPHON) ELSE ISNULL(C4.HAND_PHON, C4.TELEPHONE_NUM1) END) WHEN 'Y' THEN F.RTELNO END) AS RTELNO /* 전자문서핸드폰 */ , 'N' AS REVERSEYN /* 역발행여부(Y:역발행대상 세금계산서) */ , '' AS SENDID /* 공급자 SENDBIL ID */ , '' AS RECVID /* 공급받는자 SENDBIL ID */ , A.DIV_CODE AS DIV_CODE /* 사업장코드 */ , A.SALE_DIV_CODE AS SALE_DIV_CODE /* 사업장코드 */ , M3.REF_CODE1 AS DEL_YN /* 삭제가능여부 */ , A.COMP_CODE , C.BILL_MEM_TYPE AS BILL_MEM_TYPE /* 회원구분 */ , CONVERT(NVARCHAR(08), GETDATE(), 112) AS CREATE_DT /* 생성된 일자 */ , A.BFO_ISSU_ID AS REPORT_ETC01 /* 당초승인번호 */ , '' AS ERR_GUBUN /* 에러구분 */ , A.BEFORE_PUB_NUM /* 수정전세금계산서번호 */ , A.ORIGINAL_PUB_NUM /* 원본세금계산서번호 */ , A.PLUS_MINUS_TYPE /* 계산서 구분('+','-') */ , (CASE WHEN A.PLUS_MINUS_TYPE = '-' THEN 1 ELSE 2 END) AS SEQ_GUBUN /* 순번정열 */ FROM (/* 1.1 영업 세금계산서 테이블 */ SELECT A.COMP_CODE , A.DIV_CODE , A.SALE_DIV_CODE , A.EB_NUM , A.BILL_DATE , A.CUSTOM_CODE , (CASE WHEN @BillTypeYn = 'Y' THEN (CASE WHEN ISNULL(M1.REF_CODE1,'')='' THEN A.BILL_TYPE ELSE ISNULL(M1.REF_CODE1,'') END) ELSE A.BILL_TYPE END) AS BILL_TYPE , A.BILL_SEND_YN , A.BILL_FLAG , A.SALE_LOC_AMT_I , A.TAX_AMT_O , A.PUB_NUM , A.MODI_REASON , A.REMARK , A.BILL_GUBUN , CONVERT(NVARCHAR, '1') AS CRT_LOC , ISNULL(A.SALE_PRSN, '') AS SALE_PRSN , A.BEFORE_PUB_NUM , A.ORIGINAL_PUB_NUM , A.PLUS_MINUS_TYPE , A.BFO_ISSU_ID , A.COMPANY_NUM , A.CUSTOM_NAME , A.TOP_NAME , A.ADDR , A.COMP_CLASS , A.COMP_TYPE , A.SERVANT_COMPANY_NUM , A.RECEIVE_PRSN_NAME , A.RECEIVE_PRSN_EMAIL , A.RECEIVE_PRSN_TEL , A.RECEIVE_PRSN_MOBL FROM STB100T A WITH (NOLOCK) LEFT JOIN BCM100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BCM100T C1 WITH (NOLOCK) ON C1.COMP_CODE = C.COMP_CODE AND C1.CUSTOM_CODE = C.MANAGE_CUSTOM LEFT JOIN BILL_DB.BILL.BILL_STAT D ON D.BILLSEQ = A.EB_NUM LEFT JOIN BSA100T M1 WITH (NOLOCK) ON M1.COMP_CODE = A.COMP_CODE AND M1.SUB_CODE = A.BILL_TYPE AND M1.MAIN_CODE = 'S024' LEFT JOIN BSA100T M2 WITH (NOLOCK) ON M2.COMP_CODE = A.COMP_CODE AND M2.MAIN_CODE = 'S099' AND M2.SUB_CODE = '1' WHERE A.COMP_CODE = @CompCode AND A.PLUS_MINUS_TYPE = N'+' AND C.BILL_PUBLISH_TYPE = N'1' AND ((ISNULL(A.AC_DATE, '') > N'' AND M2.REF_CODE1 = 'Y') OR (M2.REF_CODE1 IS NULL OR M2.REF_CODE1 = 'N' OR M2.REF_CODE1 = ''))
AND A.DIV_CODE = #{DIV_CODE}
AND A.BILL_DATE >= #{BILL_DATE_FR}
AND A.BILL_DATE <= #{BILL_DATE_TO}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND C.CUSTOM_NAME = #{CUSTOM_NAME}
AND A.BILL_TYPE = #{BILL_TYPE}
AND D.BILLSTAT = #{BILLSTAT}
AND A.BILL_SEND_YN = #{BILL_SEND_YN}
AND C.BILL_MEM_TYPE = #{BILL_MEM_TYPE}
AND C1.CUSTOM_CODE LIKE #{MANAGE_CUST_CD} +'%'
AND C1.CUSTOM_NAME LIKE '%'+#{MANAGE_CUST_NM} +'%'
AND CONVERT(VARCHAR(8), A.INSERT_DB_TIME, 112) >= #{INSERT_DB_TIME_FR}
AND CONVERT(VARCHAR(8), A.INSERT_DB_TIME, 112) <= #{INSERT_DB_TIME_TO}
AND A.REMARK LIKE '%'+ #{REMARK} +'%'
AND (A.SALE_PRSN = #{SALE_PRSN} OR C.BUSI_PRSN = #{SALE_PRSN} )
UNION ALL
/* 1.2 회계 세금계산서 테이블 */ SELECT A.COMP_CODE , A.DIV_CODE , A.SALE_DIV_CODE , A.EB_NUM , A.BILL_DATE , A.CUSTOM_CODE , A.BILL_TYPE , A.BILL_SEND_YN , A.BILL_FLAG , A.SALE_LOC_AMT_I , A.TAX_AMT_O , A.PUB_NUM , A.MODI_REASON , A.REMARK , A.BILL_GUBUN , CONVERT(NVARCHAR, '3') AS CRT_LOC , ISNULL(A.SALE_PRSN, '') AS SALE_PRSN , A.BEFORE_PUB_NUM , A.ORIGINAL_PUB_NUM , A.PLUS_MINUS_TYPE , A.BFO_ISSU_ID , A.COMPANY_NUM , A.CUSTOM_NAME , A.TOP_NAME , A.ADDR , A.COMP_CLASS , A.COMP_TYPE , A.SERVANT_COMPANY_NUM , A.RECEIVE_PRSN_NAME , A.RECEIVE_PRSN_EMAIL , A.RECEIVE_PRSN_TEL , A.RECEIVE_PRSN_MOBL FROM ATX110T A WITH (NOLOCK) LEFT JOIN BCM100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BCM100T C1 WITH (NOLOCK) ON C1.COMP_CODE = C.COMP_CODE AND C1.CUSTOM_CODE = C.MANAGE_CUSTOM LEFT JOIN BILL_DB.BILL.BILL_STAT D ON D.BILLSEQ = A.EB_NUM LEFT JOIN BSA100T M2 WITH (NOLOCK) ON M2.COMP_CODE = A.COMP_CODE AND M2.MAIN_CODE = 'S099' AND M2.SUB_CODE = '3' WHERE A.COMP_CODE = @CompCode AND A.PLUS_MINUS_TYPE = N'+' AND C.BILL_PUBLISH_TYPE = N'1' AND ((ISNULL(A.AC_DATE, '') > N'' AND M2.REF_CODE1 = 'Y') OR (M2.REF_CODE1 IS NULL OR M2.REF_CODE1 = 'N' OR M2.REF_CODE1 = ''))
AND A.DIV_CODE = #{DIV_CODE}
AND A.BILL_DATE >= #{BILL_DATE_FR}
AND A.BILL_DATE <= #{BILL_DATE_TO}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND C.CUSTOM_NAME = #{CUSTOM_NAME}
AND A.BILL_TYPE = #{BILL_TYPE}
AND D.BILLSTAT = #{BILLSTAT}
AND A.BILL_SEND_YN = #{BILL_SEND_YN}
AND C.BILL_MEM_TYPE = #{BILL_MEM_TYPE}
AND C1.CUSTOM_CODE LIKE #{MANAGE_CUST_CD} +'%'
AND C1.CUSTOM_NAME LIKE '%'+ #{MANAGE_CUST_NM} +'%'
AND CONVERT(VARCHAR(8), A.INSERT_DB_TIME, 112) >= #{INSERT_DB_TIME_FR}
AND CONVERT(VARCHAR(8), A.INSERT_DB_TIME, 112) <= #{INSERT_DB_TIME_TO}
AND A.REMARK LIKE %'+ #{REMARK} +'%'
AND A.SALE_PRSN = #{SALE_PRSN}
UNION ALL
/* 1.3 무역 세금계산서 테이블 */ SELECT A.COMP_CODE , A.DIV_CODE , A.SALE_DIV_CODE , A.EB_NUM , A.BILL_DATE , A.CUSTOM_CODE , A.BILL_TYPE , A.BILL_SEND_YN , A.BILL_FLAG , A.SALE_LOC_AMT_I , A.TAX_AMT_O , A.PUB_NUM , A.MODI_REASON , A.REMARK , A.BILL_GUBUN , CONVERT(NVARCHAR, '5') AS CRT_LOC , ISNULL(A.SALE_PRSN, '') AS SALE_PRSN , A.BEFORE_PUB_NUM , A.ORIGINAL_PUB_NUM , A.PLUS_MINUS_TYPE , A.BFO_ISSU_ID , A.COMPANY_NUM , A.CUSTOM_NAME , A.TOP_NAME , A.ADDR , A.COMP_CLASS , A.COMP_TYPE , A.SERVANT_COMPANY_NUM , A.RECEIVE_PRSN_NAME , A.RECEIVE_PRSN_EMAIL , A.RECEIVE_PRSN_TEL , A.RECEIVE_PRSN_MOBL FROM TTB100T A WITH (NOLOCK) LEFT JOIN BCM100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BCM100T C1 WITH (NOLOCK) ON C1.COMP_CODE = C.COMP_CODE AND C1.CUSTOM_CODE = C.MANAGE_CUSTOM LEFT JOIN BILL_DB.BILL.BILL_STAT D ON D.BILLSEQ = A.EB_NUM LEFT JOIN BSA100T M2 WITH (NOLOCK) ON M2.COMP_CODE = A.COMP_CODE AND M2.MAIN_CODE = 'S099' AND M2.SUB_CODE = '5' WHERE A.COMP_CODE = @CompCode AND A.PLUS_MINUS_TYPE = N'+' AND C.BILL_PUBLISH_TYPE = N'1' AND ((ISNULL(A.AC_DATE, '') > N'' AND M2.REF_CODE1 = 'Y') OR (M2.REF_CODE1 IS NULL OR M2.REF_CODE1 = 'N' OR M2.REF_CODE1 = ''))
AND A.DIV_CODE = #{DIV_CODE}
AND A.BILL_DATE >= #{BILL_DATE_FR}
AND A.BILL_DATE <= #{BILL_DATE_TO}
AND A.CUSTOM_CODE = #{CUSTOM_CODE}
AND C.CUSTOM_NAME = #{CUSTOM_NAME}
AND A.BILL_TYPE = #{BILL_TYPE}
AND D.BILLSTAT = #{BILLSTAT}
AND A.BILL_SEND_YN = #{BILL_SEND_YN}
AND C.BILL_MEM_TYPE = #{BILL_MEM_TYPE}
AND C1.CUSTOM_CODE LIKE #{MANAGE_CUST_CD}
AND C1.CUSTOM_NAME LIKE #{MANAGE_CUST_NM}
AND CONVERT(VARCHAR(8), A.INSERT_DB_TIME, 112) >= #{INSERT_DB_TIME_FR}
AND CONVERT(VARCHAR(8), A.INSERT_DB_TIME, 112) <= #{INSERT_DB_TIME_TO}
AND A.REMARK LIKE '%'+ #{REMARK} +'%'
AND A.SALE_PRSN = #{SALE_PRSN}
) A LEFT JOIN (SELECT MAX(SEND_LOG_TIME) AS SEND_LOG_TIME , DIV_CODE , EB_NUM , BILL_DATE , CUSTOM_CODE , MAX(EB_STEP) AS EB_STEP , COMP_CODE FROM STB200T WITH (NOLOCK) WHERE COMP_CODE = @CompCode
AND DIV_CODE = #{DIV_CODE}
AND EB_STEP IN('1', '2') GROUP BY COMP_CODE, DIV_CODE, EB_NUM, BILL_DATE, CUSTOM_CODE) B ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.EB_NUM = A.EB_NUM AND B.BILL_DATE = A.BILL_DATE AND B.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BCM100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BILL_DB.BILL.BILL_STAT D ON D.BILLSEQ = A.EB_NUM LEFT JOIN BILL_DB.BILL.BILL_TRANS F ON F.BILLSEQ = A.EB_NUM LEFT JOIN BOR120T C1 WITH (NOLOCK) ON C1.COMP_CODE = A.COMP_CODE AND C1.DIV_CODE = A.DIV_CODE LEFT JOIN BOR100T C2 WITH (NOLOCK) ON C2.COMP_CODE = A.COMP_CODE LEFT JOIN BSA100T M1 WITH (NOLOCK) ON M1.COMP_CODE = A.COMP_CODE AND M1.MAIN_CODE = 'B066' AND M1.SUB_CODE != '$' AND M1.SUB_CODE = (CASE WHEN A.BILL_TYPE = '12' THEN '11' ELSE A.BILL_TYPE END) LEFT JOIN BSA100T M2 WITH (NOLOCK) ON M2.COMP_CODE = A.COMP_CODE AND M2.MAIN_CODE = 'B087' AND M2.SUB_CODE != '$' AND M2.SUB_CODE = A.BILL_SEND_YN LEFT JOIN BSA100T M3 WITH (NOLOCK) ON M3.COMP_CODE = A.COMP_CODE AND M3.MAIN_CODE = 'S050' AND M3.SUB_CODE != '$' AND M3.SUB_CODE = D.BILLSTAT LEFT JOIN (SELECT A.COMP_CODE , A.CUSTOM_CODE , A.PRSN_NAME , A.MAIL_ID , A.HAND_PHON , A.TELEPHONE_NUM1 FROM BCM120T A WITH(NOLOCK) INNER JOIN (SELECT A.COMP_CODE , MAX(A.CUSTOM_CODE) AS CUSTOM_CODE , MIN(A.SEQ) AS SEQ FROM BCM120T AS A WITH(NOLOCK) WHERE A.MAIN_BILL_YN ='Y' AND A.BILL_TYPE IN ('1','3') GROUP BY A.COMP_CODE, A.CUSTOM_CODE) B ON B.COMP_CODE = A.COMP_CODE AND B.CUSTOM_CODE = A.CUSTOM_CODE AND B.SEQ = A.SEQ) C4 ON C4.COMP_CODE = A.COMP_CODE AND C4.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BSA100T E1 WITH (NOLOCK) ON E1.COMP_CODE = A.COMP_CODE AND E1.MAIN_CODE = 'S010' AND E1.SUB_CODE != '$' AND E1.SUB_CODE = A.SALE_PRSN LEFT JOIN BSA100T E2 WITH (NOLOCK) ON E2.COMP_CODE = C.COMP_CODE AND E2.MAIN_CODE = 'S010' AND E2.SUB_CODE != '$' AND E2.SUB_CODE = C.BUSI_PRSN LEFT JOIN BSA210T E3 WITH (NOLOCK) ON E3.COMP_CODE = E1.COMP_CODE AND E3.TREE_CODE = E1.REF_CODE2 LEFT JOIN BSA210T E4 WITH (NOLOCK) ON E4.COMP_CODE = E2.COMP_CODE AND E4.TREE_CODE = E2.REF_CODE2 WHERE ISNULL(A.SALE_LOC_AMT_I, 0) + ISNULL(A.TAX_AMT_O, 0) != 0.0
AND A.CRT_LOC = #{CRT_LOC}
AND CONVERT(VARCHAR(8), B.SEND_LOG_TIME, 112) >= #{SEND_LOG_TIME_FR}
AND CONVERT(VARCHAR(8), B.SEND_LOG_TIME, 112) <= #{SEND_LOG_TIME_TO}
