/* ssa700ukrv.Cssa700ukrs1v[fnSsa700Std] Query02 */ BEGIN SET NOCOUNT ON SET ARITHABORT ON
DECLARE @CompCode NVARCHAR(08) /* 법인코드 */ , @UserId NVARCHAR(100) /* 사용자ID */ , @LangType NVARCHAR(2) /* 언어구분 */ , @RefItem NVARCHAR(01) , @DateFormat NVARCHAR(10)
DECLARE @BillTypeYn NVARCHAR(01) , @ETaxDBUser NVARCHAR(50)
SET @CompCode = #{S_COMP_CODE} SET @UserId = #{S_USER_ID} SET @LangType = #{S_LANG_CODE}
/* 명칭 참조 유형 */ SELECT TOP 1 @RefItem = REF_ITEM FROM BSA300T WITH (NOLOCK) WHERE USER_ID = @UserId
SET @RefItem = ISNULL(@RefItem, N'0')
/* 날짜 포맷 유형 설정 */ SELECT TOP 1 @DateFormat = CODE_NAME FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = N'B044' AND REF_CODE1 = N'Y'
SET @DateFormat = ISNULL(@DateFormat, 'YYYY.MM.DD')
/* BILL_TYPE 참조시 S024의 REF_CODE1의 증빙유형 사용 여부 */ SELECT @BillTypeYn = (CASE WHEN ISNULL(REF_CODE3 , '') = '' THEN 'N' ELSE REF_CODE3 END) , @ETaxDBUser = (CASE WHEN ISNULL(REF_CODE10, '') = '' THEN 'WEBCASH' ELSE REF_CODE10 END) FROM BSA100T WITH (NOLOCK) WHERE COMP_CODE = @CompCode AND MAIN_CODE = 'S084' AND SUB_CODE = '01'
SET @BillTypeYn = ISNULL(@BillTypeYn,'N') SET @BillTypeYn = (CASE WHEN @BillTypeYn = 'Y' THEN 'Y' ELSE 'N' END)
SET @ETaxDBUser = ISNULL(@ETaxDBUser,'WEBCASH')
/* 데이터 조회 */ SELECT CAST('' AS BIT) AS CHK , (CASE WHEN D.ERR_CD != '000000' AND D.ERR_CD != 'SEND' THEN 'Error' ELSE M2.CODE_NAME END) AS TRANSYN_NAME /* 전송여부명 */ , D.STAT_CODE AS STAT_CODE /* 상태 */ , M3.CODE_NAME AS STS /* 상태 */ , CONVERT(NVARCHAR, A.CRT_LOC) AS CRT_LOC /* 생성경로 */ , A.BILL_FLAG AS BILL_FLAG /* 세금계산서구분 */ , (CASE WHEN A.BILL_FLAG = '1' THEN (CASE WHEN A.BILL_TYPE = '11' THEN '0101' /* 과세 */ WHEN A.BILL_TYPE = '20' THEN '0301' /* 면세 */ WHEN A.BILL_TYPE = '12' THEN '0102' /* 영세율 */ END) WHEN A.BILL_FLAG = '2' THEN (CASE WHEN A.BILL_TYPE = '11' THEN '0201' /* 과세 */ WHEN A.BILL_TYPE = '20' THEN '0401' /* 면세 */ WHEN A.BILL_TYPE = '12' THEN '0202' /* 영세율 */ END) END) AS TAX_TYPE /* 세금계산서종류 */ , (CASE ISNULL(A.BILL_SEND_YN, 'N') WHEN 'N' THEN A.BILL_GUBUN WHEN 'Y' THEN D.POPS_CODE END) AS POPS_CODE /* 영수/청구 구분 */ , (CASE WHEN ISNULL(A.BILL_DATE, '') = '' THEN '' ELSE REPLACE(REPLACE(REPLACE(@DateFormat, 'YYYY', SUBSTRING(A.BILL_DATE, 1, 4)) , 'MM' , SUBSTRING(A.BILL_DATE, 5, 2)) , 'DD' , SUBSTRING(A.BILL_DATE, 7, 2)) END) AS REGS_DATE /* 발행일 */ , C1.COMPANY_NUM AS SELR_CORP_NO /* 공급자 사업자번호 */ , (CASE WHEN ISNULL(C1.DIV_FULL_NAME, '') = '' THEN C1.DIV_NAME ELSE C1.DIV_FULL_NAME END) AS SELR_CORP_NM /* 공급자 업체명 */ , C1.SUB_DIV_NUM AS SELR_CODE /* 공급자 종사업장번호*/ , C1.REPRE_NAME AS SELR_CEO /* 공급자 대표자명 */ , C1.COMP_TYPE AS SELR_BUSS_CONS /* 공급자 업태 */ , C1.COMP_CLASS AS SELR_BUSS_TYPE /* 공급자 업종 */ , C1.ADDR AS SELR_ADDR /* 공급자 주소 */ , (CASE WHEN ISNULL(E1.CODE_NAME, '') != '' THEN E1.CODE_NAME ELSE (CASE WHEN ISNULL(E2.CODE_NAME, '') != '' THEN E2.CODE_NAME ELSE '' END) END) AS SELR_CHRG_NM /* 공급자 담당자명 */ , (CASE WHEN ISNULL(E3.TREE_NAME, '') != '' THEN E3.TREE_NAME ELSE (CASE WHEN ISNULL(E4.TREE_NAME, '') != '' THEN E4.TREE_NAME ELSE '' END) END) AS SELR_CHRG_POST /* 공급자 부서명 */ , (CASE WHEN ISNULL(E1.REF_CODE6, '') != '' THEN E1.REF_CODE6 ELSE (CASE WHEN ISNULL(E2.REF_CODE6, '') != '' THEN E2.REF_CODE6 ELSE C1.TELEPHON END) END) AS SELR_CHRG_TEL /* 공급자 전화번호 */ ,