AND DIV_CODE =#{DIV_CODE}
AND EB_STEP IN('1', '2') GROUP BY COMP_CODE, DIV_CODE, EB_NUM, BILL_DATE, CUSTOM_CODE) B ON B.COMP_CODE = A.COMP_CODE AND B.DIV_CODE = A.DIV_CODE AND B.EB_NUM = A.EB_NUM AND B.BILL_DATE = A.BILL_DATE AND B.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BCM100T C WITH (NOLOCK) ON C.COMP_CODE = A.COMP_CODE AND C.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN WEBCASH_DB.WEBCASH.ITIS_ISSU_MSTR AS D ON D.ISSU_SEQNO = A.EB_NUM LEFT JOIN BOR120T C1 WITH (NOLOCK) ON C1.COMP_CODE = A.COMP_CODE AND C1.DIV_CODE = A.DIV_CODE LEFT JOIN BOR100T C2 WITH (NOLOCK) ON C2.COMP_CODE = A.COMP_CODE LEFT JOIN BSA100T M1 WITH (NOLOCK) ON M1.COMP_CODE = A.COMP_CODE AND M1.MAIN_CODE = 'B066' AND M1.SUB_CODE != '$' AND M1.SUB_CODE = (CASE WHEN A.BILL_TYPE = '12' THEN '11' ELSE A.BILL_TYPE END) LEFT JOIN BSA100T M2 WITH (NOLOCK) ON M2.COMP_CODE = A.COMP_CODE AND M2.MAIN_CODE = 'B087' AND M2.SUB_CODE != '$' AND M2.SUB_CODE = A.BILL_SEND_YN LEFT JOIN BSA100T M3 WITH (NOLOCK) ON M3.COMP_CODE = A.COMP_CODE AND M3.MAIN_CODE = 'S080' AND M3.SUB_CODE != '$' AND M3.SUB_CODE = D.STAT_CODE LEFT JOIN BSA100T M4 WITH (NOLOCK) ON M4.COMP_CODE = A.COMP_CODE AND M4.MAIN_CODE = 'S081' AND M4.SUB_CODE != '$' AND M4.SUB_CODE = D.REQ_STAT_CODE LEFT JOIN (SELECT A.COMP_CODE , A.CUSTOM_CODE , A.PRSN_NAME , A.MAIL_ID , A.HAND_PHON , A.TELEPHONE_NUM1 FROM BCM120T A WITH(NOLOCK) INNER JOIN (SELECT A.COMP_CODE , MAX(A.CUSTOM_CODE) AS CUSTOM_CODE , MIN(A.SEQ) AS SEQ FROM BCM120T AS A WITH(NOLOCK) WHERE A.MAIN_BILL_YN ='Y' AND A.BILL_TYPE IN ('1','3') GROUP BY A.COMP_CODE, A.CUSTOM_CODE) B ON B.COMP_CODE = A.COMP_CODE AND B.CUSTOM_CODE = A.CUSTOM_CODE AND B.SEQ = A.SEQ) C4 ON C4.COMP_CODE = A.COMP_CODE AND C4.CUSTOM_CODE = A.CUSTOM_CODE LEFT JOIN BSA100T E1 WITH (NOLOCK) ON E1.COMP_CODE = A.COMP_CODE AND E1.MAIN_CODE = 'S010' AND E1.SUB_CODE != '$' AND E1.SUB_CODE = A.SALE_PRSN LEFT JOIN BSA100T E2 WITH (NOLOCK) ON E2.COMP_CODE = C.COMP_CODE AND E2.MAIN_CODE = 'S010' AND E2.SUB_CODE != '$' AND E2.SUB_CODE = C.BUSI_PRSN LEFT JOIN BSA210T E3 WITH (NOLOCK) ON E3.COMP_CODE = E1.COMP_CODE AND E3.TREE_CODE = E1.REF_CODE2 LEFT JOIN BSA210T E4 WITH (NOLOCK) ON E4.COMP_CODE = E2.COMP_CODE AND E4.TREE_CODE = E2.REF_CODE2 LEFT JOIN BSA100T E5 WITH (NOLOCK) ON E5.COMP_CODE = A.COMP_CODE AND E5.MAIN_CODE = 'S083' AND E5.SUB_CODE != '$' AND E5.SUB_CODE = '01' LEFT JOIN BSA100T E6 WITH (NOLOCK) ON E6.COMP_CODE = A.COMP_CODE AND E6.MAIN_CODE = 'S083' AND E6.SUB_CODE != '$' AND E6.SUB_CODE = '02' LEFT JOIN BSA100T E7 WITH (NOLOCK) ON E7.COMP_CODE = A.COMP_CODE AND E7.MAIN_CODE = 'S083' AND E7.SUB_CODE != '$' AND E7.SUB_CODE = '03' WHERE ISNULL(A.SALE_LOC_AMT_I, 0) + ISNULL(A.TAX_AMT_O, 0) != 0.0
AND A.CRT_LOC = #{CRT_LOC}
AND CONVERT(VARCHAR(8), B.SEND_LOG_TIME, 112) >= #{SEND_LOG_TIME_FR}
AND CONVERT(VARCHAR(8), B.SEND_LOG_TIME, 112) <= #{SEND_LOG_TIME_TO}
ORDER BY A.BILL_DATE, A.CUSTOM_CODE, SEQ_GUBUN
SET NOCOUNT OFF SET ARITHABORT OFF END

mapper.update:
{call SP_SALES_ElectronicTaxInvoiceOfSendBill  (
	#{KEY_VALUE, mode=IN, jdbcType=VARCHAR, javaType=java.lang.String},
	#{ErrorDesc, mode=OUT, jdbcType=VARCHAR, javaType=java.lang.String} 
)}

