SELECT A.FID AS id
     , A.FID AS fid
     , A.ORIGINAL_FILE_NAME AS name
     , B.SIZE as size
     , '6' AS status
  FROM      SOF102T A WITH(NOLOCK)
 INNER JOIN BFL200T B WITH(NOLOCK) ON A.COMP_CODE = B.COMP_CODE AND A.FID = B.FID
 WHERE A.COMP_CODE = #{S_COMP_CODE}
   AND A.DOC_NO    = #{DOC_NO}
